import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Documents.css';

function Documents() {
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));

  const fetchDocuments = useCallback(async () => {
    if (!user || !user.id || !user.role) {
      console.error('Отсутствуют данные пользователя');
      setError('Ошибка авторизации');
      setLoading(false);
      return;
    }

    try {
      console.log('Отправка запроса документов:', { 
        userId: user.id, 
        role: user.role,
        workerId: user.workerId 
      });

      const response = await axios.get(`http://localhost:5000/api/documents`, {
        params: {
          userId: user.id,
          role: user.role
        },
        timeout: 5000 // Добавляем таймаут запроса
      });

      if (!response.data) {
        throw new Error('Нет данных в ответе');
      }

      console.log('Получены документы:', response.data.length);
      setDocuments(response.data);
      setLoading(false);
    } catch (err) {
      console.error('Ошибка при загрузке документов:', err);
      setError(err.response?.data?.error || 'Ошибка при загрузке документов');
      setLoading(false);
    }
  }, [user]);

  useEffect(() => {
    fetchDocuments();
  }, [fetchDocuments]);

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
  };

  const handleBack = () => {
    navigate(user.role === 1 ? '/admin' : '/user');
  };

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="documents-container">
      <div className="documents-header">
        <h1>Документы</h1>
        <div className="header-buttons">
          <button onClick={handleBack} className="back-button">Назад</button>
          <button onClick={handleLogout} className="logout-button">Выйти</button>
        </div>
      </div>

      <div className="documents-list">
        {documents.length === 0 ? (
          <p>Документы не найдены</p>
        ) : (
          documents.map((doc) => (
            <div key={doc.ID_Document} className="document-card">
              <h3>{doc.ProgramName}</h3>
              <p><strong>Регистрационный номер:</strong> {doc.regnumber}</p>
              <p><strong>Серия:</strong> {doc.formseries}</p>
              <p><strong>Номер:</strong> {doc.formnumber}</p>
              <p><strong>Дата выдачи:</strong> {new Date(doc.DateIssue).toLocaleDateString()}</p>
              <p><strong>Срок действия:</strong> {new Date(doc.DataEnd).toLocaleDateString()}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Documents;
