import React, { useState, useEffect, useMemo, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './AdminPanel.css';

// --- Компонент для формы добавления (Программы, Организации, KKS) ---
const AddItemForm = ({ type, onSave, onCancel, programTypes = [], allOrganizations = [] }) => {
    const [newItem, setNewItem] = useState({});

    useEffect(() => {
        setNewItem({}); // Сброс формы при изменении типа
    }, [type]);

    const handleChange = (field, value) => {
        setNewItem(prev => ({ ...prev, [field]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave(newItem);
        setNewItem({});
    };

    const handleCancel = (e) => {
        e.preventDefault();
        setNewItem({});
        onCancel();
    };

    // Форма для Программ
    if (type === 'programs') { // Изменил 'program' на 'programs' для соответствия activeTab
        return (
            <form onSubmit={handleSubmit} className="add-form">
                <h4>Добавить программу</h4>
                <input
                    type="text" placeholder="Название программы" required
                    value={newItem.Name_ProgDPO || ''}
                    onChange={(e) => handleChange('Name_ProgDPO', e.target.value)} />
                {/* Выбор Типа Программы */}
                <select required value={newItem.id_type || ''} onChange={(e) => handleChange('id_type', e.target.value)}>
                    <option value="" disabled>Выберите тип</option>
                    {/* Убедитесь, что programTypes содержит { id_type, Name_Type } */}
                    {programTypes.map(pt => <option key={pt.id_type} value={pt.id_type}>{pt.Name_Type}</option>)}
                </select>
                {/* Выбор Организации */}
                <select required value={newItem.ID_Org || ''} onChange={(e) => handleChange('ID_Org', e.target.value)}>
                    <option value="" disabled>Выберите организацию</option>
                    {/* Убедитесь, что allOrganizations содержит { ID_Org, FName } */}
                    {allOrganizations.map(org => <option key={org.ID_Org} value={org.ID_Org}>{org.FName}</option>)}
                </select>
                <div className="form-actions">
                    <button type="submit">Сохранить</button>
                    <button type="button" onClick={handleCancel}>Отмена</button>
                </div>
            </form>
        );
    }

    // Форма для Организаций
    if (type === 'organizations') { // Изменил 'organization' на 'organizations'
       return (
            <form onSubmit={handleSubmit} className="add-form">
                <h4>Добавить организацию</h4>
                <input
                    type="text" placeholder="Полное название" required
                    value={newItem.FName || ''}
                    onChange={(e) => handleChange('FName', e.target.value)} />
                 <input
                    type="text" placeholder="Краткое название" required
                    value={newItem.SName || ''}
                    onChange={(e) => handleChange('SName', e.target.value)} />
                 <div className="form-actions">
                    <button type="submit">Сохранить</button>
                    <button type="button" onClick={handleCancel}>Отмена</button>
                 </div>
            </form>
        );
    }

     // Форма для KKS
     if (type === 'kks') {
        return (
            <form onSubmit={handleSubmit} className="add-form">
                <h4>Добавить KKS</h4>
                <input
                    type="text" placeholder="Название KKS" required
                    value={newItem.Name_KKS || ''}
                    onChange={(e) => handleChange('Name_KKS', e.target.value)} />
                 <input
                    type="text" placeholder="Краткое обозначение"
                    value={newItem.SName_KKS || ''}
                    onChange={(e) => handleChange('SName_KKS', e.target.value)} />
                 <div className="form-actions">
                    <button type="submit">Сохранить</button>
                    <button type="button" onClick={handleCancel}>Отмена</button>
                 </div>
            </form>
        );
    }

    return null; // Если тип не совпал
};

// --- Компонент формы для Работников (бывшие Пользователи) ---
// Управляет записями в таблице 'worker'
const WorkerForm = ({ initialData = {}, onSave, onCancel, isEditing = false }) => {
    const [workerData, setWorkerData] = useState(initialData);
    const [newPassword, setNewPassword] = useState(''); // Для изменения пароля

    useEffect(() => {
        // Инициализируем или сбрасываем состояние формы
        // Убедимся, что Role имеет значение по умолчанию или сохраняется
        setWorkerData({
            ...initialData,
            Role: initialData.Role !== undefined ? initialData.Role : '' // Значение по умолчанию для select
        });
        setNewPassword(''); // Сброс поля нового пароля
    }, [initialData]);

    const handleChange = (field, value) => {
        setWorkerData(prev => ({ ...prev, [field]: value }));
    };

    const handlePasswordChange = (e) => {
        setNewPassword(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const dataToSend = { ...workerData };

        // Добавляем пароль только если он введен (при редактировании) или при создании
        if (newPassword) {
            dataToSend.Password = newPassword; // Отправляем как Password (бэкенд должен его захэшировать)
        } else if (!isEditing) {
            // Пароль обязателен при создании нового работника (логина)
            alert('Пароль обязателен при создании пользователя.');
            return;
        }

        // Убираем поле FName_Worker если оно пришло из списка (чтобы не отправлять обратно)
        // Бэкенд сам добавит/обновит его при необходимости, если форма позволяет его менять
        // delete dataToSend.FName_Worker; // Если FName_Worker редактируется в форме, не удалять

        onSave(dataToSend); // Отправляем данные на сохранение/добавление
    };

    const handleCancelClick = (e) => {
        e.preventDefault();
        onCancel(); // Вызов функции отмены
    };

    return (
        <form onSubmit={handleSubmit} className="add-form user-form"> {/* Оставляем класс user-form для стилей */}
            <h4>{isEditing ? `Редактировать работника (ID: ${workerData.ID_Worker})` : 'Добавить работника'}</h4>

             {/* Поле для ФИО Работника */}
             <input
                type="text" placeholder="ФИО Работника" required
                value={workerData.FName_Worker || ''}
                onChange={(e) => handleChange('FName_Worker', e.target.value)} />

            {/* Поле для Логина */}
            <input
                type="text" placeholder="Логин" required
                value={workerData.Login || ''}
                onChange={(e) => handleChange('Login', e.target.value)} />

            {/* Поле для Пароля */}
            <input
                type="password"
                placeholder={isEditing ? 'Новый пароль (оставьте пустым, чтобы не менять)' : 'Пароль'}
                value={newPassword}
                onChange={handlePasswordChange}
                required={!isEditing} // Обязательно только при создании
            />

            {/* Выбор Роли */}
            <select required value={workerData.Role} onChange={(e) => handleChange('Role', parseInt(e.target.value, 10))}>
                <option value="" disabled>Выберите роль</option>
                <option value={0}>Работник ОУ</option> {/* Роль 0 */}
                <option value={1}>Работник ЦДО (Админ)</option> {/* Роль 1 */}
            </select>

            {/* Привязка к работнику здесь не нужна, т.к. мы редактируем самого работника */}
            {/* Убрал select для ID_Worker */}

            <div className="form-actions">
               <button type="submit">Сохранить</button>
               <button type="button" onClick={handleCancelClick}>Отмена</button>
            </div>
        </form>
    );
};


// --- Основной компонент AdminPanel ---
function AdminPanel() {
    // Состояния
    const [activeTab, setActiveTab] = useState('programs'); // 'programs', 'organizations', 'kks', 'workers'
    const [programs, setPrograms] = useState([]);
    const [organizations, setOrganizations] = useState([]);
    const [kks, setKks] = useState([]);
    const [workers, setWorkers] = useState([]); // Теперь это список работников для вкладки "Работники"
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [editingItem, setEditingItem] = useState(null); // Объект для редактирования
    const [addingItemType, setAddingItemType] = useState(null); // Тип добавляемого элемента ('programs', 'organizations', 'kks', 'workers')
    const [programTypes, setProgramTypes] = useState([]); // Для формы добавления программ
    const [allOrganizations, setAllOrganizations] = useState([]); // Для формы добавления программ
    // const [allWorkers, setAllWorkers] = useState([]); // Убрали, т.к. теперь есть основное состояние workers
    const [sortConfig, setSortConfig] = useState({ key: null, direction: 'ascending' });

    // Хуки
    const navigate = useNavigate();
    const apiUrl = process.env.REACT_APP_API_URL;

    // Получение пользователя из localStorage
    const user = useMemo(() => {
        const storedUser = localStorage.getItem('user');
        try {
            return storedUser ? JSON.parse(storedUser) : null;
        } catch (e) {
            console.error("Failed to parse user from localStorage", e);
            localStorage.removeItem('user'); // Очистка некорректных данных
            return null;
        }
    }, []);

    // --- Сортировка ---
    const requestSort = (key) => {
        let direction = 'ascending';
        if (sortConfig.key === key && sortConfig.direction === 'ascending') {
            direction = 'descending';
        }
        setSortConfig({ key, direction });
    };

    // Определение ключа ID для текущей вкладки
    const getIdKeyForTab = (tab) => {
        switch (tab) {
            case 'programs': return 'ID_ProgDPO';
            case 'organizations': return 'ID_Org';
            case 'kks': return 'ID_KKS';
            case 'workers': return 'ID_Worker'; // ИЗМЕНЕНО: Ключ для работников
            default: return null;
        }
    };

    // Сброс сортировки при смене вкладки
    useEffect(() => {
        const idKey = getIdKeyForTab(activeTab);
        if (idKey) {
            setSortConfig({ key: idKey, direction: 'ascending' });
        } else {
            setSortConfig({ key: null, direction: 'ascending' });
        }
         // Сброс форм при смене вкладки
        setEditingItem(null);
        setAddingItemType(null);
    }, [activeTab]);

    // --- Загрузка данных ---
    const fetchData = useCallback(async (forceAux = false) => {
        // Проверка на админа
        if (!user || user.role !== 1) {
            console.warn("fetchData called without admin user, aborting.");
            setLoading(false);
            setError("Доступ запрещен. Требуются права администратора.");
            return; // Выход, если не админ
        }
        setLoading(true);
        setError(''); // Сброс ошибки перед загрузкой
        console.log(`Workspaceing data. Active tab: ${activeTab}`);

        // Загрузка вспомогательных данных (типы программ, все организации) один раз или принудительно
        if (forceAux || programTypes.length === 0 || allOrganizations.length === 0) {
            try {
                console.log("Fetching auxiliary data (types, all orgs)...");
                const endpoints = [
                    `${apiUrl}/api/program-types`,
                    `${apiUrl}/api/organizations`, // Загружаем все организации один раз
                ];
                const [typesRes, orgsRes] = await Promise.all(
                    endpoints.map(url => axios.get(url))
                );
                setProgramTypes(typesRes.data || []);
                setAllOrganizations(orgsRes.data || []);
                console.log("Auxiliary data fetched.");
            } catch (err) {
                console.error('Error fetching auxiliary data:', err);
                const auxErrorMsg = 'Ошибка загрузки вспомогательных данных (типы, организации).';
                setError(prev => prev ? `${prev}\n${auxErrorMsg}` : auxErrorMsg);
                // Не прерываем загрузку основных данных, если вспомогательные не загрузились
            }
        }

        // Загрузка основных данных для текущей вкладки
        try {
            let response;
            let endpoint;
            switch (activeTab) {
                case 'programs': endpoint = `${apiUrl}/api/programs`; break;
                case 'organizations': endpoint = `${apiUrl}/api/organizations`; break; // Используем уже загруженные allOrganizations? Или обновим? Обновим.
                case 'kks': endpoint = `${apiUrl}/api/kks`; break;
                case 'workers': endpoint = `${apiUrl}/api/workers-admin`; break; // ИЗМЕНЕНО: Используем эндпоинт для всех работников
                default:
                    console.warn(`Unknown activeTab: ${activeTab}`);
                    setLoading(false);
                    return; // Выход, если вкладка неизвестна
            }
            console.log(`Attempting to fetch ${endpoint}...`);
            response = await axios.get(endpoint);
            console.log(`Received response from ${endpoint}`, response.status);

            // Обновление состояния в зависимости от вкладки
            switch (activeTab) {
                case 'programs': setPrograms(response.data || []); break;
                case 'organizations': setOrganizations(response.data || []); setAllOrganizations(response.data || []); break; // Обновляем и основной и вспомогательный список
                case 'kks': setKks(response.data || []); break;
                case 'workers': setWorkers(response.data || []); break; // ИЗМЕНЕНО: Обновляем состояние workers
            }
        } catch (err) {
            console.error(`Error fetching ${activeTab}:`, err.response || err.message || err);
            const mainErrorMsg = `Ошибка загрузки данных для вкладки "${activeTab}".`;
            setError(prev => prev ? `${prev}\n${mainErrorMsg}` : mainErrorMsg);
        } finally {
            console.log(`Setting loading to false for ${activeTab}.`);
            setLoading(false); // Снимаем флаг загрузки в любом случае
        }
    }, [activeTab, user, apiUrl, programTypes.length, allOrganizations.length]); // Зависимости useCallback

    // Первичная загрузка и проверка авторизации
    useEffect(() => {
        if (!user || user.role !== 1) {
            navigate('/login'); // Перенаправление, если не админ
            return; // Прерываем выполнение эффекта
        }
        fetchData(true); // Вызов загрузки данных при монтировании и смене пользователя/fetchData
    }, [user, navigate, fetchData]); // Зависимости useEffect

    // --- Мемоизированные отсортированные списки ---
    const sortedPrograms = useMemo(() => {
         // ... (логика сортировки осталась прежней)
         let sortableItems = [...programs];
         if (sortConfig.key) {
             sortableItems.sort((a, b) => {
                 if (a[sortConfig.key] < b[sortConfig.key]) {
                     return sortConfig.direction === 'ascending' ? -1 : 1;
                 }
                 if (a[sortConfig.key] > b[sortConfig.key]) {
                     return sortConfig.direction === 'ascending' ? 1 : -1;
                 }
                 return 0;
             });
         }
         return sortableItems;
    }, [programs, sortConfig]);

    const sortedOrganizations = useMemo(() => {
        // ... (логика сортировки осталась прежней)
         let sortableItems = [...organizations];
         if (sortConfig.key) {
             sortableItems.sort((a, b) => {
                 const valA = a[sortConfig.key];
                 const valB = b[sortConfig.key];
                 const compareResult = typeof valA === 'number' && typeof valB === 'number'
                     ? valA - valB
                     : String(valA).localeCompare(String(valB));
                 return sortConfig.direction === 'ascending' ? compareResult : -compareResult;
             });
         }
         return sortableItems;
    }, [organizations, sortConfig]);

    const sortedKks = useMemo(() => {
        // ... (логика сортировки осталась прежней)
         let sortableItems = [...kks];
         if (sortConfig.key) {
             sortableItems.sort((a, b) => {
                 const valA = a[sortConfig.key];
                 const valB = b[sortConfig.key];
                 const compareResult = typeof valA === 'number' && typeof valB === 'number'
                     ? valA - valB
                     : String(valA).localeCompare(String(valB));
                 return sortConfig.direction === 'ascending' ? compareResult : -compareResult;
             });
         }
         return sortableItems;
    }, [kks, sortConfig]);

    const sortedWorkers = useMemo(() => { // ИЗМЕНЕНО: sortedUsers -> sortedWorkers
        let sortableItems = [...workers]; // ИЗМЕНЕНО: users -> workers
        if (sortConfig.key) {
            sortableItems.sort((a, b) => {
                const valA = a[sortConfig.key];
                const valB = b[sortConfig.key];

                 // Специальная сортировка для Роли (числовая)
                if (sortConfig.key === 'Role' && typeof valA === 'number' && typeof valB === 'number') {
                    return sortConfig.direction === 'ascending' ? valA - valB : valB - valA;
                }

                // Сортировка по ФИО (строковая, регистронезависимая)
                if (sortConfig.key === 'FName_Worker') {
                    const nameA = String(valA ?? '').toLowerCase();
                    const nameB = String(valB ?? '').toLowerCase();
                    if (nameA < nameB) return sortConfig.direction === 'ascending' ? -1 : 1;
                    if (nameA > nameB) return sortConfig.direction === 'ascending' ? 1 : -1;
                    return 0;
                }

                 // Общая сортировка (числовая или строковая)
                const compareResult = typeof valA === 'number' && typeof valB === 'number'
                    ? valA - valB
                    : String(valA ?? '').localeCompare(String(valB ?? ''));

                return sortConfig.direction === 'ascending' ? compareResult : -compareResult;
            });
        }
        return sortableItems;
    }, [workers, sortConfig]); // ИЗМЕНЕНО: users -> workers

    // --- Обработчики CRUD ---

    const handleEdit = (item) => {
        console.log("Editing item:", item);
        setEditingItem(item); // Установка объекта для редактирования
        setAddingItemType(null); // Сброс режима добавления
    };

    const handleCancel = () => {
        setEditingItem(null); // Сброс редактируемого объекта
    };

    // Обработчик изменения полей в inline-редактировании (для Программ, Орг, ККС)
    const handleChangeInline = (field, value) => {
        setEditingItem(prev => ({ ...prev, [field]: value }));
    };

    // Сохранение изменений (PUT)
    const handleSave = async (editedData) => {
        // Используем переданные данные (из WorkerForm) или данные из editingItem (для inline)
        const itemToSave = editedData || editingItem;
        if (!itemToSave) return;

        const idKey = getIdKeyForTab(activeTab);
        const id = itemToSave[idKey];
        if (id === undefined) {
            console.error("Cannot save item without ID", itemToSave);
            setError("Не удалось определить ID для сохранения.");
            return;
        }

        console.log(`Saving edited item (ID: ${id}, Tab: ${activeTab}):`, itemToSave);
        setLoading(true);
        setError('');
        try {
            let endpoint;
            // Определяем эндпоинт для PUT запроса
            switch (activeTab) {
                case 'programs': endpoint = `${apiUrl}/api/programs/${id}`; break;
                case 'organizations': endpoint = `${apiUrl}/api/organizations/${id}`; break;
                case 'kks': endpoint = `${apiUrl}/api/kks/${id}`; break;
                case 'workers': endpoint = `${apiUrl}/api/workers/${id}`; break; // ИЗМЕНЕНО: Нужен PUT /api/workers/:id на бэкенде
                default: throw new Error("Неизвестная вкладка для сохранения");
            }

             // ВАЖНО: Для workers, если отправляется пароль, бэкенд должен его хэшировать!
             // Данные itemToSave уже должны содержать 'Password', если он менялся (из WorkerForm)

            await axios.put(endpoint, itemToSave);
            setEditingItem(null); // Выход из режима редактирования
            await fetchData(); // Обновление данных
            console.log(`Item (ID: ${id}) saved successfully.`);
        } catch (err) {
            console.error('Ошибка при сохранении изменений:', err.response?.data || err.message || err);
            setError(err.response?.data?.error || 'Ошибка при сохранении данных');
        } finally {
            setLoading(false);
        }
    };

    // Показ формы добавления
    const handleShowAddForm = () => {
        setAddingItemType(activeTab); // Установка типа добавляемого элемента
        setEditingItem(null); // Сброс режима редактирования
    };

    // Отмена добавления
    const handleCancelAdd = () => {
        setAddingItemType(null); // Сброс режима добавления
    };

    // Добавление нового элемента (POST)
    const handleAddItem = async (newItemData) => {
        const type = addingItemType; // Тип из состояния addingItemType
        if (!type) return;
        console.log(`Adding new ${type}:`, newItemData);
        setLoading(true);
        setError('');
        try {
            let endpoint;
            // Определяем эндпоинт для POST запроса
            switch (type) {
                case 'programs': endpoint = `${apiUrl}/api/programs`; break;
                case 'organizations': endpoint = `${apiUrl}/api/organizations`; break;
                case 'kks': endpoint = `${apiUrl}/api/kks`; break;
                case 'workers': endpoint = `${apiUrl}/api/workers`; break; // ИЗМЕНЕНО: Нужен POST /api/workers на бэкенде
                default: throw new Error("Неизвестный тип для добавления");
            }

             // ВАЖНО: Для workers, бэкенд должен хэшировать пароль перед сохранением!
             // newItemData уже содержит 'Password' из WorkerForm

            await axios.post(endpoint, newItemData);
            setAddingItemType(null); // Выход из режима добавления
            await fetchData(); // Обновление данных
            console.log(`Item of type ${type} added successfully.`);
        } catch (err) {
            console.error(`Ошибка при добавлении ${type}:`, err.response?.data || err.message || err);
            setError(err.response?.data?.error || `Ошибка при добавлении элемента`);
        } finally {
            setLoading(false);
        }
    };

    // Удаление элемента (DELETE)
    const handleDelete = async (idToDelete) => {
        if (!window.confirm('Вы уверены, что хотите удалить этот элемент?')) {
            return; // Отмена, если пользователь не подтвердил
        }
        const idKey = getIdKeyForTab(activeTab);
        console.log(`Deleting item with ID: ${idToDelete} (Key: ${idKey}) on tab: ${activeTab}`);
        setLoading(true);
        setError('');
        try {
            let endpoint;
             // Определяем эндпоинт для DELETE запроса
            switch (activeTab) {
                case 'programs': endpoint = `${apiUrl}/api/programs/${idToDelete}`; break;
                case 'organizations': endpoint = `${apiUrl}/api/organizations/${idToDelete}`; break;
                case 'kks': endpoint = `${apiUrl}/api/kks/${idToDelete}`; break;
                case 'workers': endpoint = `${apiUrl}/api/workers/${idToDelete}`; break; // ИЗМЕНЕНО: Нужен DELETE /api/workers/:id на бэкенде
                default: throw new Error("Неизвестная вкладка для удаления");
            }
            await axios.delete(endpoint);
            // Сброс редактирования, если удалялся редактируемый элемент
            if (editingItem && editingItem[idKey] === idToDelete) {
                setEditingItem(null);
            }
            await fetchData(); // Обновление данных
            console.log(`Item (ID: ${idToDelete}) deleted successfully.`);
        } catch (err) {
            console.error(`Ошибка при удалении элемента (ID: ${idToDelete}):`, err.response?.data || err.message || err);
            setError(err.response?.data?.error || 'Ошибка при удалении элемента. Возможно, на него есть ссылки.');
        } finally {
            setLoading(false);
        }
    };

    // --- Вспомогательные функции рендеринга ---

    // Отображение роли
    const displayRole = (roleValue) => {
        // return roleValue === 1 ? 'Администратор' : roleValue === 0 ? 'Работник' : 'Неизвестно';
        return roleValue === 1 ? 'Работник ЦДО (Админ)' : roleValue === 0 ? 'Работник ОУ' : 'Неизвестно'; // Обновил названия
    };

    // Рендеринг заголовка таблицы с сортировкой
    const renderSortableHeader = (label, key) => (
        <th onClick={() => requestSort(key)} className="sortable-header">
            {label}
            {/* Индикаторы сортировки */}
            {sortConfig.key === key ? (sortConfig.direction === 'ascending' ? ' ▲' : ' ▼') : ' ↕'}
        </th>
    );

    // --- Основной рендеринг компонента ---

    // Начальная загрузка или нет пользователя
    if (!user && loading) return <div className="loading">Проверка авторизации и загрузка данных...</div>;
    // Если после загрузки пользователя нет (ошибка парсинга или не вошел)
    if (!user) return <div className="error">Не удалось получить данные пользователя. <button onClick={() => navigate('/login')}>Войти</button></div>;
    // Если пользователь не админ (дополнительная проверка)
    if (user.role !== 1) return <div className="error">Доступ запрещен.</div>;

    // Индикатор загрузки (если не ошибка)
    const showLoadingIndicator = loading && !error;

    return (
        <div className="admin-panel">
            {/* Шапка */}
            <div className="admin-header">
                <h1>Панель администратора</h1>
                <div className="header-buttons">
                   <button onClick={() => navigate('/admin')} className="back-button">Назад</button> {/* Всегда возвращает на /admin */}
                   <button onClick={() => { localStorage.removeItem('user'); navigate('/login'); }} className="logout-button">Выйти</button>
                </div>
            </div>

            {/* Вкладки */}
            <div className="admin-tabs">
                <button className={`tab-button ${activeTab === 'programs' ? 'active' : ''}`} onClick={() => setActiveTab('programs')}>Программы</button>
                <button className={`tab-button ${activeTab === 'organizations' ? 'active' : ''}`} onClick={() => setActiveTab('organizations')}>Организации</button>
                <button className={`tab-button ${activeTab === 'kks' ? 'active' : ''}`} onClick={() => setActiveTab('kks')}>ККС</button>
                <button className={`tab-button ${activeTab === 'workers' ? 'active' : ''}`} onClick={() => setActiveTab('workers')}>Работники</button> {/* ИЗМЕНЕНО: users -> workers */}
            </div>

            {/* Индикатор загрузки и ошибки */}
            {showLoadingIndicator && <div className="loading-inline">Обновление данных...</div>}
            {error && <div className="error-inline">{error} <button onClick={() => setError('')} title="Закрыть ошибку">×</button></div>}

            {/* Основной контент */}
            <div className="admin-content">

                 {/* Форма Добавления/Редактирования Работника */}
                 {(addingItemType === 'workers' || (editingItem && activeTab === 'workers')) && (
                     <WorkerForm
                         // Уникальный ключ для сброса состояния при смене редактируемого элемента
                         key={editingItem ? editingItem.ID_Worker : 'add-worker'}
                         initialData={editingItem || {}} // Передаем данные для редактирования или пустой объект
                         onSave={editingItem ? handleSave : handleAddItem} // Разные функции для сохранения
                         onCancel={editingItem ? handleCancel : handleCancelAdd} // Разные функции для отмены
                         isEditing={!!editingItem} // Флаг режима редактирования
                     />
                 )}

                {/* Форма Добавления (Программы, Организации, KKS) */}
                {addingItemType && addingItemType !== 'workers' && (
                    <AddItemForm
                        key={`add-${addingItemType}`} // Ключ для сброса формы
                        type={addingItemType}
                        onSave={handleAddItem}
                        onCancel={handleCancelAdd}
                        programTypes={programTypes} // Передаем типы
                        allOrganizations={allOrganizations} // Передаем организации
                    />
                 )}


                {/* Кнопка "Добавить", если не режим редактирования или добавления */}
                {!editingItem && !addingItemType && (
                    <button onClick={handleShowAddForm} className="add-button">
                        Добавить {
                            activeTab === 'programs' ? 'программу' :
                            activeTab === 'organizations' ? 'организацию' :
                            activeTab === 'kks' ? 'KKS' :
                            activeTab === 'workers' ? 'работника' : '' // ИЗМЕНЕНО: users -> workers
                        }
                    </button>
                )}

                {/* Таблица Программ */}
                {activeTab === 'programs' && (
                    <table className="admin-table">
                        <thead>
                            <tr>
                                {renderSortableHeader('ID', 'ID_ProgDPO')}
                                {renderSortableHeader('Название', 'Name_ProgDPO')}
                                {renderSortableHeader('Тип', 'Name_Type')}
                                <th>Действия</th>
                            </tr>
                        </thead>
                        <tbody>
                            {sortedPrograms.map(program => (
                                <tr key={program.ID_ProgDPO}>
                                    {/* Режим Редактирования */}
                                    {editingItem?.ID_ProgDPO === program.ID_ProgDPO ? (
                                        <>
                                            <td>{program.ID_ProgDPO}</td>
                                            <td>
                                                {/* Поле Названия */}
                                                <input
                                                    type="text"
                                                    value={editingItem.Name_ProgDPO}
                                                    onChange={(e) => handleChangeInline('Name_ProgDPO', e.target.value)}
                                                />
                                            </td>
                                            <td>
                                                {/* Тип не редактируем inline */}
                                                {program.Name_Type || 'N/A'}
                                            </td>
                                            <td>
                                                <button onClick={() => handleSave()}>Сохранить</button>
                                                <button onClick={handleCancel}>Отмена</button>
                                            </td>
                                        </>
                                    ) : (
                                        /* Режим Просмотра */
                                        <>
                                            <td>{program.ID_ProgDPO}</td>
                                            <td>{program.Name_ProgDPO}</td>
                                            <td>{program.Name_Type || 'N/A'}</td>
                                            <td className="actions-cell">
                                                <button onClick={() => handleEdit(program)} disabled={!!addingItemType || !!editingItem}>Редакт.</button>
                                                <button onClick={() => handleDelete(program.ID_ProgDPO)} className="delete-button" disabled={!!addingItemType || !!editingItem}>Удалить</button>
                                            </td>
                                        </>
                                    )}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}

                 {/* Таблица Организаций */}
                 {activeTab === 'organizations' && (
                     <table className="admin-table">
                         <thead>
                             <tr>
                                 {renderSortableHeader('ID', 'ID_Org')}
                                 {renderSortableHeader('Полное название', 'FName')}
                                 {renderSortableHeader('Краткое название', 'SName')}
                                 <th>Действия</th>
                             </tr>
                         </thead>
                         <tbody>
                             {sortedOrganizations.map(org => (
                                 <tr key={org.ID_Org}>
                                     {editingItem?.ID_Org === org.ID_Org ? (
                                         <>
                                             <td>{org.ID_Org}</td>
                                             <td>
                                                 <input type="text" value={editingItem.FName} onChange={(e) => handleChangeInline('FName', e.target.value)} />
                                             </td>
                                             <td>
                                                 <input type="text" value={editingItem.SName} onChange={(e) => handleChangeInline('SName', e.target.value)} />
                                             </td>
                                             <td>
                                                 <button onClick={handleSave}>Сохранить</button>
                                                 <button onClick={handleCancel}>Отмена</button>
                                             </td>
                                         </>
                                     ) : (
                                         <>
                                             <td>{org.ID_Org}</td>
                                             <td>{org.FName}</td>
                                             <td>{org.SName}</td>
                                             <td className="actions-cell">
                                                 <button onClick={() => handleEdit(org)} disabled={!!addingItemType || !!editingItem}>Редакт.</button>
                                                 <button onClick={() => handleDelete(org.ID_Org)} className="delete-button" disabled={!!addingItemType || !!editingItem}>Удалить</button>
                                             </td>
                                         </>
                                     )}
                                 </tr>
                             ))}
                         </tbody>
                     </table>
                 )}

                 {/* Таблица KKS */}
                 {activeTab === 'kks' && (
                     <table className="admin-table">
                         <thead>
                             <tr>
                                 {renderSortableHeader('ID', 'ID_KKS')}
                                 {renderSortableHeader('Название', 'Name_KKS')}
                                 {renderSortableHeader('Краткое обозначение', 'SName_KKS')}
                                 <th>Действия</th>
                             </tr>
                         </thead>
                         <tbody>
                             {sortedKks.map(kksItem => (
                                 <tr key={kksItem.ID_KKS}>
                                     {editingItem?.ID_KKS === kksItem.ID_KKS ? (
                                         <>
                                             <td>{kksItem.ID_KKS}</td>
                                             <td>
                                                 <input type="text" value={editingItem.Name_KKS} onChange={(e) => handleChangeInline('Name_KKS', e.target.value)} />
                                             </td>
                                             <td>
                                                 <input type="text" value={editingItem.SName_KKS} onChange={(e) => handleChangeInline('SName_KKS', e.target.value)} />
                                             </td>
                                             <td>
                                                 <button onClick={handleSave}>Сохранить</button>
                                                 <button onClick={handleCancel}>Отмена</button>
                                             </td>
                                         </>
                                     ) : (
                                         <>
                                             <td>{kksItem.ID_KKS}</td>
                                             <td>{kksItem.Name_KKS}</td>
                                             <td>{kksItem.SName_KKS}</td>
                                             <td className="actions-cell">
                                                 <button onClick={() => handleEdit(kksItem)} disabled={!!addingItemType || !!editingItem}>Редакт.</button>
                                                 <button onClick={() => handleDelete(kksItem.ID_KKS)} className="delete-button" disabled={!!addingItemType || !!editingItem}>Удалить</button>
                                             </td>
                                         </>
                                     )}
                                 </tr>
                             ))}
                         </tbody>
                     </table>
                 )}

                 {/* Таблица Работников */}
                 {activeTab === 'workers' && ( 
                     <table className="admin-table">
                         <thead>
                             <tr>
                                 {renderSortableHeader('ID', 'ID_Worker')}
                                 {renderSortableHeader('Логин', 'Login')}
                                 {renderSortableHeader('ФИО Работника', 'FName_Worker')}
                                 {renderSortableHeader('Роль', 'Role')}
                                 <th>Действия</th>
                             </tr>
                         </thead>
                         <tbody>
                             {/* Используем sortedWorkers */}
                             {sortedWorkers.map(w => (
                                 // Редактирование работников теперь через WorkerForm, не inline
                                 <tr key={w.ID_Worker}>
                                      <td>{w.ID_Worker}</td>
                                      <td>{w.Login}</td>
                                      <td>{w.FName_Worker}</td>
                                      <td>{displayRole(w.Role)}</td>
                                      <td className="actions-cell">
                                          {/* Кнопка редактирования открывает WorkerForm */}
                                          <button onClick={() => handleEdit(w)} disabled={!!addingItemType || !!editingItem}>Редакт.</button>
                                          {/* Кнопка удаления */}
                                          <button onClick={() => handleDelete(w.ID_Worker)} className="delete-button" disabled={!!addingItemType || !!editingItem}>Удалить</button>
                                      </td>
                                  </tr>
                             ))}
                         </tbody>
                     </table>
                 )}

            </div> {/* admin-content */}
        </div> // admin-panel
    );
}

export default AdminPanel;
