import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './User.css';

const apiUrl = process.env.REACT_APP_API_URL;

// Helper function to check if training is needed (без изменений)
const isTrainingNeeded = (lastTrainingDateStr) => {
    if (!lastTrainingDateStr) {
        return true; // No training date found, assume training is needed
    }
    try {
        const lastTrainingDate = new Date(lastTrainingDateStr);
        const threeYearsAgo = new Date();
        threeYearsAgo.setFullYear(threeYearsAgo.getFullYear() - 3);

        if (isNaN(lastTrainingDate.getTime())) {
            console.warn("Invalid date encountered:", lastTrainingDateStr);
            return true; // Treat invalid date as needing training
        }

        return lastTrainingDate < threeYearsAgo;
    } catch (e) {
        console.error("Error parsing date:", lastTrainingDateStr, e);
        return true; // Error parsing date, assume training needed
    }
};

// Helper function to format date for display (без изменений)
const formatDate = (dateStr) => {
    if (!dateStr) return 'Нет данных';
    try {
        const date = new Date(dateStr);
        if (isNaN(date.getTime())) return 'Некорректная дата';
        return date.toLocaleDateString('ru-RU'); // Format to DD.MM.YYYY
    } catch (e) {
        return 'Ошибка формата';
    }
}

function User() {
    const navigate = useNavigate();
    // Убрали состояние 'workers'
    const [user, setUser] = useState(null); // Состояние для пользователя
    const [kksStatus, setKksStatus] = useState([]);
    const [isLoading, setIsLoading] = useState(true); // Начинаем с загрузки
    const [error, setError] = useState(null);
    const [recommendedPrograms, setRecommendedPrograms] = useState([]);
    const [isRecLoading, setIsRecLoading] = useState(false);
    const [recError, setRecError] = useState(null);

    // Эффект для загрузки данных пользователя из localStorage при монтировании
    useEffect(() => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            try {
                const parsedUser = JSON.parse(storedUser);
                 // ВАЖНО: Убедитесь, что FName_Worker сохраняется при логине!
                if (!parsedUser.FName_Worker) {
                    console.warn("FName_Worker отсутствует в данных пользователя из localStorage.");
                    // Можно добавить логику для его дозагрузки, если необходимо, но лучше исправить логин.
                }
                setUser(parsedUser);
            } catch (e) {
                console.error("Ошибка парсинга пользователя из localStorage:", e);
                localStorage.removeItem('user'); // Удаляем некорректные данные
                setError("Ошибка данных пользователя. Пожалуйста, войдите снова.");
                setIsLoading(false);
            }
        } else {
            setError("Пользователь не аутентифицирован.");
            setIsLoading(false);
            // navigate('/login'); // Возможно, перенаправить на логин
        }
    }, []); // Пустой массив зависимостей - выполняется один раз при монтировании

    // Мемоизированный список ККС, требующих обучения
    const kksNeedingTraining = useMemo(() => {
        if (!Array.isArray(kksStatus)) return [];
        return kksStatus.filter(kks => isTrainingNeeded(kks.LastTrainingDate));
    }, [kksStatus]);

    // Объединенный useEffect для загрузки KKS статуса и Рекомендаций
    useEffect(() => {
        // Выполняется только если данные пользователя загружены
        if (!user || !user.workerId) {
             // Если изначально не было user, ошибка уже установлена в первом useEffect
             // Если user пропал позже (маловероятно), можно установить ошибку здесь
             if (user === null && !isLoading) { // Проверка, что это не начальная загрузка
                 setError("Данные пользователя отсутствуют.");
             }
            return; // Выход, если нет ID работника
        }

        // Проверка роли - загружаем только для обычных работников
        if (user.role !== 0) {
            console.log("Пользователь не является работником (role != 0), данные ККС не запрашиваются.");
            setKksStatus([]);
            setRecommendedPrograms([]);
            setIsLoading(false); // Устанавливаем флаг загрузки в false
            return;
        }

        const fetchData = async () => {
            setIsLoading(true); // Основная загрузка началась
            setError(null);
            setKksStatus([]); // Сброс перед загрузкой
            setRecommendedPrograms([]); // Сброс перед загрузкой
            setRecError(null);
            setIsRecLoading(false);

            try {
                // 1. Загрузка статуса ККС
                console.log(`Workspaceing KKS status for workerId: ${user.workerId}`);
                const kksResponse = await axios.get(`${apiUrl}/api/user-kks-status?userId=${user.workerId}`);
                const fetchedKksStatus = kksResponse.data || [];
                setKksStatus(fetchedKksStatus);
                console.log("KKS Status Response:", fetchedKksStatus);

                // 2. Проверка и загрузка Рекомендаций (если нужно)
                const needingTraining = fetchedKksStatus.filter(kks => isTrainingNeeded(kks.LastTrainingDate));

                if (needingTraining.length > 0) {
                    setIsRecLoading(true); // Загрузка рекомендаций началась
                    const needingKksIds = needingTraining.map(kks => kks.ID_KKS);
                    console.log(`Workspaceing recommended programs for KKS IDs: [${needingKksIds.join(',')}]`);
                    try {
                        const recResponse = await axios.get(`${apiUrl}/api/recommended-programs?kksIds=${needingKksIds.join(',')}`);
                        console.log("Recommended Programs Response:", recResponse.data);
                        setRecommendedPrograms(recResponse.data || []);
                    } catch (recErr) {
                        console.error('Ошибка при загрузке рекомендуемых программ:', recErr);
                        setRecError(recErr.response?.data?.error || 'Не удалось загрузить рекомендуемые программы.');
                        setRecommendedPrograms([]); // Убедимся, что пусто при ошибке
                    } finally {
                        setIsRecLoading(false); // Загрузка рекомендаций завершена (успех/ошибка)
                    }
                } else {
                    console.log("Обучение по ККС не требуется, рекомендации не запрашиваются.");
                    setRecommendedPrograms([]); // Обучение не нужно - список рекомендаций пуст
                }

            } catch (kksErr) {
                console.error('Ошибка при загрузке статуса ККС:', kksErr);
                setError(kksErr.response?.data?.error || 'Не удалось загрузить статус обучения по ККС.');
                setKksStatus([]); // Убедимся, что пусто при ошибке
                setRecommendedPrograms([]); // И рекомендации тоже
            } finally {
                setIsLoading(false); // Основная загрузка завершена (успех/ошибка)
            }
        };

        fetchData();

    }, [user, apiUrl]); // Зависимость от user (и apiUrl)

    // Обработчики навигации и выхода
    const handleLogout = () => {
        localStorage.removeItem('user');
        setUser(null); // Очищаем состояние пользователя
        navigate('/login');
    };

    const handleViewDocuments = () => {
        navigate('/documents'); // Предполагаем, что /documents использует workerId из localStorage
    };

    const handleViewAnalysis = () => {
        navigate('/analysis'); // Предполагаем, что /analysis использует workerId из localStorage
    };

    // --- Рендеринг ---

    // Отображение начальной загрузки или основной ошибки
    if (isLoading && !user) { // Показываем загрузку, пока user не определен
        return <div className="loading">Загрузка данных пользователя...</div>;
    }
     if (error && !isLoading) { // Показываем ошибку, если она есть и загрузка завершена
         return <div className="error-container"><div className="error">{error}</div> <button onClick={() => navigate('/login')}>Войти</button></div>;
     }
     if (!user) { // Если пользователя так и нет (не должно случиться при нормальной работе)
        return <div className="error-container"><div className="error">Не удалось загрузить данные пользователя.</div><button onClick={() => navigate('/login')}>Войти</button></div>;
     }


    // Основной рендеринг для аутентифицированного пользователя
    return (
        <div className="user-container">
            <div className="user-header">
                <h1>Личный кабинет</h1>
                <button onClick={handleLogout} className="logout-button">Выйти</button>
            </div>

            <div className="user-content">
                {/* Информация о пользователе */}
                <div className="user-info">
                    <h2>Информация о пользователе</h2>
                    {/* Отображаем имя из объекта user */}
                    <p><strong>ФИО: </strong> {user?.FName_Worker || 'Имя не найдено'}</p>
                    <p><strong>Логин:</strong> {user?.login || 'Логин не найден'}</p>
                    <p><strong>Роль:</strong> {user?.role === 0 ? 'Работник ОУ' : user?.role === 1 ? 'Работник ЦДО (Админ)' : 'Неизвестная роль'}</p> {/* Более точное отображение роли */}
                </div>

                {/* Уведомления по ККС (только для работников) */}
                {user?.role === 0 && (
                    <div className="user-kks-notifications">
                        <h2>Статус обучения по ККС</h2>
                        {/* Показываем общую загрузку или загрузку ККС */}
                        {isLoading && <p>Загрузка статуса ККС...</p>}
                        {/* Показываем ошибку загрузки ККС */}
                        {error && <p className="error-message">Ошибка загрузки статуса ККС: {error}</p>}
                        {/* Отображаем статус, если нет загрузки и ошибки ККС */}
                        {!isLoading && !error && (
                            <>
                                {kksNeedingTraining.length > 0 ? (
                                    <>
                                        <p className="notification-warning"><strong>Внимание!</strong> Требуется пройти обучение по следующим критериям ККС:</p>
                                        <ul>
                                            {kksNeedingTraining.map(kks => (
                                                <li key={kks.ID_KKS}>
                                                    <strong>{kks.KKS_FullName} ({kks.KKS_ShortName})</strong>
                                                    <br />
                                                    <small>Последнее обучение: {formatDate(kks.LastTrainingDate)} </small>
                                                </li>
                                            ))}
                                        </ul>
                                    </>
                                ) : (
                                    <p className="notification-success">Все необходимые обучения по ККС пройдены своевременно.</p>
                                )}
                                {/* Сообщение, если ККС вообще нет */}
                                {kksStatus.length === 0 && !isLoading && <p>Нет данных о прохождении обучения по ККС.</p>}
                            </>
                        )}
                    </div>
                )}

                {/* Рекомендуемые программы (только для работников, если есть ККС для обучения) */}
                {user?.role === 0 && kksNeedingTraining.length > 0 && (
                    <div className="user-recommended-programs">
                        <h2>Рекомендуемые программы ДПО</h2>
                         {/* Показываем загрузку рекомендаций */}
                        {isRecLoading && <p>Загрузка рекомендуемых программ...</p>}
                         {/* Показываем ошибку загрузки рекомендаций */}
                        {recError && <p className="error-message">Ошибка загрузки рекомендаций: {recError}</p>}
                         {/* Отображаем рекомендации, если нет загрузки и ошибки */}
                        {!isRecLoading && !recError && (
                            <>
                                {recommendedPrograms.length > 0 ? (
                                    <ul>
                                        {recommendedPrograms.map(program => (
                                            <li key={program.ID_ProgDPO} className="program-item">
                                                <strong>{program.Name_ProgDPO}</strong>
                                                <br />
                                                <small>
                                                    Организация: {program.OrgSName || program.OrgFName || 'Не указана'}
                                                    {' | '}
                                                    Тип: {program.Name_Type || 'Не указан'}
                                                </small>
                                            </li>
                                        ))}
                                    </ul>
                                ) : (
                                    <p>Подходящих программ ДПО для требуемых ККС не найдено.</p>
                                )}
                            </>
                        )}
                    </div>
                )}

                {/* Кнопки действий */}
                <div className="user-actions">
                    <button onClick={handleViewDocuments} className="action-button">
                        Просмотр Моих Документов
                    </button>
                    <button onClick={handleViewAnalysis} className="action-button">
                        Просмотр Анализа
                    </button>
                </div>
            </div>
        </div>
    );
}

export default User;
