import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './AdminPanel.css';

function AdminPanel() {
  const [activeTab, setActiveTab] = useState('programs');
  const [programs, setPrograms] = useState([]);
  const [organizations, setOrganizations] = useState([]);
  const [kks, setKks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [editingItem, setEditingItem] = useState(null);
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    if (user?.role !== 1) {
      navigate('/login');
      return;
    }
    fetchData();
  }, [activeTab, user, navigate]);

  const fetchData = async () => {
    try {
      setLoading(true);
      let response;
      switch (activeTab) {
        case 'programs':
          response = await axios.get('http://localhost:5000/api/programs');
          setPrograms(response.data);
          break;
        case 'organizations':
          response = await axios.get('http://localhost:5000/api/organizations');
          setOrganizations(response.data);
          break;
        case 'kks':
          response = await axios.get('http://localhost:5  000/api/kks');
          setKks(response.data);
          break;
        default:
          break;
      }
      setError('');
    } catch (err) {
      setError(err.response?.data?.error || 'Ошибка при загрузке данных');
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (item) => {
    setEditingItem(item);
  };

  const handleSave = async () => {
    try {
      let endpoint;
      switch (activeTab) {
        case 'programs':
          endpoint = `${apiUrl}/api/programs/${editingItem.ID_ProgDPO}`;
          await axios.put(endpoint, editingItem);
          break;
        case 'organizations':
          endpoint = `${apiUrl}/api/organizations/${editingItem.ID_Org}`;
          await axios.put(endpoint, editingItem);
          break;
        case 'kks':
          endpoint = `${apiUrl}/api/kks/${editingItem.ID_KKS}`;
          await axios.put(endpoint, editingItem);
          break;
        default:
          break;
      }
      setEditingItem(null);
      fetchData();
    } catch (err) {
      setError(err.response?.data?.error || 'Ошибка при сохранении данных');
    }
  };

  const handleCancel = () => {
    setEditingItem(null);
  };

  const handleChange = (field, value) => {
    setEditingItem(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
  };

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="admin-panel">
      <div className="admin-header">
        <h1>Панель администратора</h1>
        <button onClick={handleLogout} className="logout-button">Выйти</button>
      </div>

      <div className="admin-tabs">
        <button 
          className={`tab-button ${activeTab === 'programs' ? 'active' : ''}`}
          onClick={() => setActiveTab('programs')}
        >
          Программы
        </button>
        <button 
          className={`tab-button ${activeTab === 'organizations' ? 'active' : ''}`}
          onClick={() => setActiveTab('organizations')}
        >
          Организации
        </button>
        <button 
          className={`tab-button ${activeTab === 'kks' ? 'active' : ''}`}
          onClick={() => setActiveTab('kks')}
        >
          ККС
        </button>
      </div>

      <div className="admin-content">
        {activeTab === 'programs' && (
          <table className="admin-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Название</th>
                <th>Тип</th>
                <th>Действия</th>
              </tr>
            </thead>
            <tbody>
              {programs.map(program => (
                <tr key={program.ID_ProgDPO}>
                  {editingItem?.ID_ProgDPO === program.ID_ProgDPO ? (
                    <>
                      <td>{program.ID_ProgDPO}</td>
                      <td>
                        <input
                          type="text"
                          value={editingItem.Name_ProgDPO}
                          onChange={(e) => handleChange('Name_ProgDPO', e.target.value)}
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={editingItem.Name_Type}
                          onChange={(e) => handleChange('Name_Type', e.target.value)}
                        />
                      </td>
                      <td>
                        <button onClick={handleSave}>Сохранить</button>
                        <button onClick={handleCancel}>Отмена</button>
                      </td>
                    </>
                  ) : (
                    <>
                      <td>{program.ID_ProgDPO}</td>
                      <td>{program.Name_ProgDPO}</td>
                      <td>{program.Name_Type}</td>
                      <td>
                        <button onClick={() => handleEdit(program)}>Редактировать</button>
                      </td>
                    </>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        )}

        {activeTab === 'organizations' && (
          <table className="admin-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Полное название</th>
                <th>Краткое название</th>
                <th>Действия</th>
              </tr>
            </thead>
            <tbody>
              {organizations.map(org => (
                <tr key={org.ID_Org}>
                  {editingItem?.ID_Org === org.ID_Org ? (
                    <>
                      <td>{org.ID_Org}</td>
                      <td>
                        <input
                          type="text"
                          value={editingItem.FName}
                          onChange={(e) => handleChange('FName', e.target.value)}
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={editingItem.SName}
                          onChange={(e) => handleChange('SName', e.target.value)}
                        />
                      </td>
                      <td>
                        <button onClick={handleSave}>Сохранить</button>
                        <button onClick={handleCancel}>Отмена</button>
                      </td>
                    </>
                  ) : (
                    <>
                      <td>{org.ID_Org}</td>
                      <td>{org.FName}</td>
                      <td>{org.SName}</td>
                      <td>
                        <button onClick={() => handleEdit(org)}>Редактировать</button>
                      </td>
                    </>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        )}

        {activeTab === 'kks' && (
          <table className="admin-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Название</th>
                <th>Краткое обозначение</th>
                <th>Действия</th>
              </tr>
            </thead>
            <tbody>
              {kks.map(kksItem => (
                <tr key={kksItem.ID_KKS}>
                  {editingItem?.ID_KKS === kksItem.ID_KKS ? (
                    <>
                      <td>{kksItem.ID_KKS}</td>
                      <td>
                        <input
                          type="text"
                          value={editingItem.Name_KKS}
                          onChange={(e) => handleChange('Name_KKS', e.target.value)}
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          value={editingItem.SName_KKS}
                          onChange={(e) => handleChange('SName_KKS', e.target.value)}
                        />
                      </td>
                      <td>
                        <button onClick={handleSave}>Сохранить</button>
                        <button onClick={handleCancel}>Отмена</button>
                      </td>
                    </>
                  ) : (
                    <>
                      <td>{kksItem.ID_KKS}</td>
                      <td>{kksItem.Name_KKS}</td>
                      <td>{kksItem.SName_KKS}</td>
                      <td>
                        <button onClick={() => handleEdit(kksItem)}>Редактировать</button>
                      </td>
                    </>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default AdminPanel;
