import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Documents.css';

function Documents() {
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/documents`, {
        params: {
          userId: user.id,
          role: user.role
        }
      });
      setDocuments(response.data);
      setLoading(false);
    } catch (err) {
      setError('Ошибка при загрузке документов');
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
  };

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="documents-container">
      <div className="documents-header">
        <h1>{user.role === 1 ? 'Все документы' : 'Мои документы'}</h1>
        <button onClick={handleLogout} className="logout-button">Выйти</button>
      </div>
      
      <div className="documents-list">
        {documents.length === 0 ? (
          <p className="no-documents">Документы не найдены</p>
        ) : (
          <table className="documents-table">
            <thead>
              <tr>
                <th>Регистрационный номер</th>
                <th>Серия формы</th>
                <th>Номер формы</th>
                <th>Дата выдачи</th>
                <th>Дата начала</th>
                <th>Дата окончания</th>
                <th>Программа</th>
                {user.role === 1 && <th>Сотрудник</th>}
              </tr>
            </thead>
            <tbody>
              {documents.map((doc) => (
                <tr key={doc.ID_Document}>
                  <td>{doc.RegNumber}</td>
                  <td>{doc.FormSeries || '-'}</td>
                  <td>{doc.FormNumber || '-'}</td>
                  <td>{new Date(doc.DateIssue).toLocaleDateString()}</td>
                  <td>{new Date(doc.DataStart).toLocaleDateString()}</td>
                  <td>{new Date(doc.DataEnd).toLocaleDateString()}</td>
                  <td>{doc.ProgramName}</td>
                  {user.role === 1 && <td>{doc.WorkerName}</td>}
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default Documents;
