import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import { Bar } from 'react-chartjs-2';
import './DocumentAnalysis.css';

// Регистрируем компоненты Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function DocumentAnalysis() {
  const [documents, setDocuments] = useState([]);
  const [workers, setWorkers] = useState([]);
  const [selectedWorker, setSelectedWorker] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [viewMode, setViewMode] = useState('analysis'); // 'analysis' или 'documents'
  const [selectedDocument, setSelectedDocument] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [programDetails, setProgramDetails] = useState(null);
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Загрузка документов
        const documentsResponse = await axios.get(`http://localhost:5000/api/documents`, {
          params: {
            userId: user.id,
            role: user.role
          }
        });
        setDocuments(documentsResponse.data);

        // Если пользователь - работник ЦДО, загружаем список работников
        if (user.role === 1) {
          const workersResponse = await axios.get('http://localhost:5000/api/workers');
          setWorkers(workersResponse.data);
          if (workersResponse.data.length > 0) {
            setSelectedWorker(workersResponse.data[0].ID_Worker);
          }
        }

        setLoading(false);
      } catch (err) {
        console.error('Ошибка при загрузке данных:', err);
        setError(err.response?.data?.error || 'Ошибка при загрузке данных');
        setLoading(false);
      }
    };

    fetchData();
  }, [user.id, user.role]);

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
  };

  const handleBack = () => {
    navigate(user.role === 1 ? '/admin' : '/user');
  };

  const handleWorkerChange = (event) => {
    setSelectedWorker(parseInt(event.target.value));
  };

  const handleViewModeChange = (mode) => {
    setViewMode(mode);
  };

  const handleShowDetails = async (document) => {
  try {
    console.log('Открытие деталей для документа:', document);
    const response = await axios.get(`http://localhost:5000/api/program-details/${document.ID_ProgDPO}`);
    console.log('Полученные детали программы:', response.data);
    setProgramDetails(response.data);
    setSelectedDocument(document);
    setShowModal(true);
  } catch (err) {
    console.error('Ошибка при загрузке деталей программы:', err);
  }
};

  const handleCloseModal = () => {
    setShowModal(false);
    setSelectedDocument(null);
    setProgramDetails(null);
  };

  // Фильтрация документов по выбранному работнику
  const filteredDocuments = user.role === 1
    ? documents.filter(doc => doc.ID_Worker === selectedWorker)
    : documents;

  // Подготовка данных для графиков
  const prepareChartData = () => {
    // Группировка документов по годам
    const documentsByYear = filteredDocuments.reduce((acc, doc) => {
      const year = new Date(doc.DateIssue).getFullYear();
      acc[year] = (acc[year] || 0) + 1;
      return acc;
    }, {});

    // Находим минимальный год из документов
    const years = Object.keys(documentsByYear).map(Number);
    const minYear = Math.min(...years);
    
    // Текущий год
    const currentYear = new Date().getFullYear();
    
    // Создаем массив всех лет от минимального до текущего
    const allYears = [];
    for (let year = minYear; year <= currentYear; year++) {
      allYears.push(year);
    }

    // Создаем массивы данных, включая нули для лет без документов
    const counts = allYears.map(year => documentsByYear[year] || 0);

    // Расчет нарастающего итога
    const cumulativeCounts = counts.reduce((acc, count, index) => {
      acc[index] = index === 0 ? count : acc[index - 1] + count;
      return acc;
    }, []);

    return {
      years: allYears,
      counts,
      cumulativeCounts
    };
  };

  const chartData = prepareChartData();

  // Настройки для графика количества документов по годам
  const yearlyData = {
    labels: chartData.years,
    datasets: [
      {
        label: user.role === 1 
          ? `Количество документов работника ${workers.find(w => w.ID_Worker === selectedWorker)?.FName_Worker || ''}`
          : 'Количество моих документов',
        data: chartData.counts,
        backgroundColor: 'rgba(54, 162, 235, 0.5)',
        borderColor: 'rgba(54, 162, 235, 1)',
        borderWidth: 1,
      },
    ],
  };

  // Настройки для графика нарастающего итога
  const cumulativeData = {
    labels: chartData.years,
    datasets: [
      {
        label: user.role === 1
          ? `Нарастающий итог документов работника ${workers.find(w => w.ID_Worker === selectedWorker)?.FName_Worker || ''}`
          : 'Нарастающий итог моих документов',
        data: chartData.cumulativeCounts,
        backgroundColor: 'rgba(75, 192, 192, 0.5)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: user.role === 1 
          ? `Анализ документов работника ${workers.find(w => w.ID_Worker === selectedWorker)?.FName_Worker || ''}`
          : 'Анализ моих документов',
      },
    },
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="analysis-container">
      <div className="analysis-header">
        <h1>Анализ документов</h1>
        <button onClick={handleBack} className="back-button">Назад</button>
      </div>
      
      {user.role === 1 && (
        <div className="worker-selector">
          <label htmlFor="worker-select">Выберите работника:</label>
          <select
            id="worker-select"
            value={selectedWorker || ''}
            onChange={handleWorkerChange}
            className="worker-select"
          >
            {workers.map(worker => (
              <option key={worker.ID_Worker} value={worker.ID_Worker}>
                {worker.FName_Worker}
              </option>
            ))}
          </select>
          <div className="view-mode-buttons">
            <button 
              className={`view-mode-button ${viewMode === 'analysis' ? 'active' : ''}`}
              onClick={() => handleViewModeChange('analysis')}
            >
              Анализ
            </button>
            <button 
              className={`view-mode-button ${viewMode === 'documents' ? 'active' : ''}`}
              onClick={() => handleViewModeChange('documents')}
            >
              Документы
            </button>
          </div>
        </div>
      )}
      
      {viewMode === 'analysis' ? (
        <div className="charts-container">
          <div className="chart-wrapper">
            <h2>Количество документов по годам</h2>
            <Bar options={options} data={yearlyData} />
          </div>
          
          <div className="chart-wrapper">
            <h2>Нарастающий итог документов по годам</h2>
            <Bar options={options} data={cumulativeData} />
          </div>
        </div>
      ) : (
        <div className="documents-list">
          <table className="documents-table">
            <thead>
              <tr>
                <th>Программа</th>
                <th>Организация</th>
                <th>Дата выдачи</th>
                <th>Действия</th>
              </tr>
            </thead>
            <tbody>
              {filteredDocuments.map((doc) => (
                <tr key={doc.ID_Document}>
                  <td>{doc.ProgramName}</td>
                  <td>{doc.SName}</td>
                  <td>{new Date(doc.DateIssue).toLocaleDateString()}</td>
                  <td>
                    <button 
                      className="details-button"
                      onClick={() => handleShowDetails(doc)}
                    >
                      Подробности
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {showModal && selectedDocument && programDetails && (
        <div className="modal-overlay">
          <div className="modal-content">
            <button className="modal-close" onClick={handleCloseModal}>×</button>
            <h2>Подробная информация о документе</h2>
            <div className="modal-details">
              <div className="detail-group">
                <h3>Основная информация</h3>
                <p><strong>Программа:</strong> {selectedDocument.ProgramName}</p>
                <p><strong>Тип программы:</strong> {programDetails.Name_Type}</p>
                <p><strong>Организация:</strong> {programDetails.FName}</p>
                <p><strong>Краткое название организации:</strong> {programDetails.SName}</p>
                <p><strong>Дата выдачи:</strong> {new Date(selectedDocument.DateIssue).toLocaleDateString()}</p>
                <p><strong>Регистрационный номер:</strong> {selectedDocument.regnumber}</p>
                <p><strong>Серия формы:</strong> {selectedDocument.formseries || '-'}</p>
                <p><strong>Номер формы:</strong> {selectedDocument.formnumber || '-'}</p>
                <p><strong>Дата начала:</strong> {new Date(selectedDocument.DataStart).toLocaleDateString()}</p>
                <p><strong>Дата окончания:</strong> {new Date(selectedDocument.DataEnd).toLocaleDateString()}</p>
              </div>
              <div className="detail-group">
                <h3>Критерии ККС</h3>
                {programDetails.name_kks ? (
                  <div>
                    <p><strong>ККС:</strong> {programDetails.name_kks}</p>
                    <p><strong>Краткие обозначения ККС:</strong> {programDetails.SName_KKS}</p>
                  </div>
                ) : (
                  <p>Критерии ККС не указаны</p>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default DocumentAnalysis;
