import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Documents.css'; // Убедитесь, что CSS файл существует

const apiUrl = process.env.REACT_APP_API_URL;

// Вспомогательная функция для форматирования даты (как в User.js/DocumentAnalysis.js)
const formatDate = (dateStr) => {
    if (!dateStr) return 'Нет данных';
    try {
        const date = new Date(dateStr);
        if (isNaN(date.getTime())) return 'Некорректная дата';
        // Формат DD.MM.YYYY
        return date.toLocaleDateString('ru-RU');
    } catch (e) {
        console.error("Ошибка форматирования даты:", dateStr, e);
        return 'Ошибка формата';
    }
};

function Documents() {
    const [documents, setDocuments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const navigate = useNavigate();
    // Используем useState для user, чтобы избежать ошибок при первом рендере, если localStorage пуст
    const [user, setUser] = useState(null);

    // Эффект для загрузки пользователя из localStorage
    useEffect(() => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            try {
                setUser(JSON.parse(storedUser));
            } catch (e) {
                console.error("Ошибка парсинга пользователя из localStorage:", e);
                localStorage.removeItem('user');
                setError("Ошибка данных пользователя. Пожалуйста, войдите снова.");
                setLoading(false); // Завершаем загрузку с ошибкой
            }
        } else {
             setError("Пользователь не аутентифицирован.");
             setLoading(false); // Завершаем загрузку с ошибкой
             // Можно добавить navigate('/login');
        }
    }, []); // Выполняется один раз при монтировании


    // Функция загрузки документов
    const fetchDocuments = useCallback(async () => {
        // Проверяем, что user загружен и содержит workerId
        if (!user || user.workerId === undefined) {
            console.log('Данные пользователя или workerId недоступны для запроса документов.');
             // Устанавливаем ошибку, только если это не первоначальное состояние (когда user еще null)
            if (user !== null) {
                 setError('Не удалось получить ID пользователя для загрузки документов.');
            }
            setLoading(false); // Завершаем загрузку
            return; // Выход, если нет workerId
        }

        setLoading(true); // Начинаем загрузку здесь
        setError(''); // Сброс ошибки

        try {
            console.log('Отправка запроса документов:', { userId: user.workerId, role: user.role }); // ИЗМЕНЕНО: user.id -> user.workerId
            const response = await axios.get(`${apiUrl}/api/documents`, {
                params: {
                    userId: user.workerId, // ИЗМЕНЕНО: user.id -> user.workerId
                    role: user.role
                }
            });
            console.log('Получены документы:', response.data);

            // Проверка, что получен массив
             if (!Array.isArray(response.data)) {
                console.error("API /api/documents не вернул массив:", response.data);
                throw new Error("Получен неверный формат данных документов.");
            }

            // Логирование номеров для отладки (опционально)
            response.data.forEach((doc, index) => {
                console.log(`Документ ${index + 1} IDs:`, {
                    regnumber: doc.regnumber,
                    formseries: doc.formseries,
                    formnumber: doc.formnumber
                });
            });

            setDocuments(response.data);

        } catch (err) {
            console.error('Ошибка при загрузке документов:', err.response || err);
            setError(err.response?.data?.error || 'Ошибка при загрузке документов');
             setDocuments([]); // Очистка документов при ошибке
        } finally {
            setLoading(false); // Завершаем загрузку в любом случае
        }
        // Зависимости useCallback теперь user.workerId и user.role
    }, [user, apiUrl]); // Зависимость от всего объекта user и apiUrl

    // Эффект для вызова fetchDocuments при изменении user или fetchDocuments
    useEffect(() => {
        // Вызываем fetchDocuments только после того, как user был успешно загружен из localStorage
        if (user) {
            fetchDocuments();
        }
    }, [user, fetchDocuments]); // Зависимость от user и fetchDocuments


    // Обработчики кнопок
    const handleLogout = () => {
        localStorage.removeItem('user');
        setUser(null); // Очищаем состояние пользователя
        navigate('/login');
    };

    const handleBack = () => {
         // Проверяем user перед использованием role
        if (user) {
            navigate(user.role === 1 ? '/admin' : '/user');
        } else {
            navigate('/login'); // Направляем на логин, если user не определен
        }
    };

    // --- Рендеринг ---

    // Состояние начальной загрузки (когда user еще null)
    if (user === null && loading) {
         return <div className="loading">Загрузка данных пользователя...</div>;
    }

     // Состояние ошибки (если user не загрузился или при запросе документов)
     if (error) {
         return (
            <div className="documents-container">
                 <div className="documents-header">
                     <h1>Ошибка</h1>
                     <div className="header-buttons">
                          {/* Кнопка Назад может работать даже при ошибке */}
                         <button onClick={handleBack} className="back-button">Назад</button>
                         <button onClick={handleLogout} className="logout-button">Выйти</button>
                     </div>
                 </div>
                 <div className="error">{error}</div>
            </div>
         );
     }

    // Состояние загрузки документов (user уже есть)
    if (loading) {
        return <div className="loading">Загрузка документов...</div>;
    }

     // Проверка на случай, если user все еще null после загрузки (не должно быть, но для надежности)
     if (!user) {
         return <div className="error">Не удалось загрузить данные пользователя. <button onClick={() => navigate('/login')}>Войти</button></div>;
     }


    // Основной рендеринг списка документов
    return (
        <div className="documents-container">
            <div className="documents-header">
                <h1>{user.role === 1 ? 'Все документы' : 'Мои документы'}</h1>
                <div className="header-buttons">
                    <button onClick={handleBack} className="back-button">Назад</button>
                    <button onClick={handleLogout} className="logout-button">Выйти</button>
                </div>
            </div>

            <div className="documents-list">
                {documents.length === 0 ? (
                    <p className="no-documents">Документы не найдены</p>
                ) : (
                    <table className="documents-table">
                        <thead>
                            <tr>
                                {user.role === 1 && <th>Сотрудник</th>}
                                <th>Программа</th>
                                <th>Регистрационный номер</th>
                                <th>Серия формы</th>
                                <th>Номер формы</th>
                                <th>Дата выдачи</th>
                                <th>Дата начала</th>
                                <th>Дата окончания</th>
                            </tr>
                        </thead>
                        <tbody>
                            {documents.map((doc) => (
                                <tr key={doc.ID_Document}>
                                    {/* Отображение имени работника для админа */}
                                    {user.role === 1 && <td>{doc.WorkerName || 'N/A'}</td>}
                                    {/* Данные документа */}
                                    <td>{doc.ProgramName || 'N/A'}</td>
                                    <td>{doc.regnumber || 'N/A'}</td>
                                    <td>{doc.formseries || 'N/A'}</td>
                                    <td>{doc.formnumber || 'N/A'}</td>
                                    {/* ИСПОЛЬЗУЕМ БЕЗОПАСНОЕ ФОРМАТИРОВАНИЕ ДАТ */}
                                    <td>{formatDate(doc.DateIssue)}</td>
                                    <td>{formatDate(doc.DataStart)}</td>
                                    <td>{formatDate(doc.DataEnd)}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );
}

export default Documents;
