import React, { useState, useEffect, useMemo, useCallback } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './AdminPanel.css';

// --- Компонент для формы добавления ---
const AddItemForm = ({ type, onSave, onCancel, programTypes = [], allOrganizations = [] }) => {
    const [newItem, setNewItem] = useState({});

    useEffect(() => {
        setNewItem({});
    }, [type]);

    const handleChange = (field, value) => {
        setNewItem(prev => ({ ...prev, [field]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSave(newItem);
        setNewItem({});
    };

    const handleCancel = (e) => {
        e.preventDefault();
        setNewItem({});
        onCancel();
    };

    if (type === 'program') {
        return (
            <form onSubmit={handleSubmit} className="add-form">
                <h4>Добавить программу</h4>
                <input
                    type="text" placeholder="Название программы" required
                    value={newItem.Name_ProgDPO || ''}
                    onChange={(e) => handleChange('Name_ProgDPO', e.target.value)} />
                <select required value={newItem.id_type || ''} onChange={(e) => handleChange('id_type', e.target.value)}>
                    <option value="" disabled>Выберите тип</option>
                    {programTypes.map(pt => <option key={pt.id_type} value={pt.id_type}>{pt.Name_Type}</option>)}
                </select>
                <select required value={newItem.ID_Org || ''} onChange={(e) => handleChange('ID_Org', e.target.value)}>
                    <option value="" disabled>Выберите организацию</option>
                    {allOrganizations.map(org => <option key={org.ID_Org} value={org.ID_Org}>{org.FName}</option>)}
                </select>
                <div className="form-actions">
                   <button type="submit">Сохранить</button>
                   <button type="button" onClick={handleCancel}>Отмена</button>
                </div>
            </form>
        );
    }

    if (type === 'organization') {
         return (
            <form onSubmit={handleSubmit} className="add-form">
                <h4>Добавить организацию</h4>
                <input
                    type="text" placeholder="Полное название" required
                    value={newItem.FName || ''}
                    onChange={(e) => handleChange('FName', e.target.value)} />
                 <input
                    type="text" placeholder="Краткое название" required
                    value={newItem.SName || ''}
                    onChange={(e) => handleChange('SName', e.target.value)} />
                 <div className="form-actions">
                   <button type="submit">Сохранить</button>
                   <button type="button" onClick={handleCancel}>Отмена</button>
                </div>
            </form>
        );
    }

     if (type === 'kks') {
        return (
            <form onSubmit={handleSubmit} className="add-form">
                <h4>Добавить KKS</h4>
                <input
                    type="text" placeholder="Название KKS" required
                    value={newItem.Name_KKS || ''}
                    onChange={(e) => handleChange('Name_KKS', e.target.value)} />
                 <input
                    type="text" placeholder="Краткое обозначение"
                    value={newItem.SName_KKS || ''}
                    onChange={(e) => handleChange('SName_KKS', e.target.value)} />
                 <div className="form-actions">
                   <button type="submit">Сохранить</button>
                   <button type="button" onClick={handleCancel}>Отмена</button>
                </div>
            </form>
        );
    }

    return null;
};

// --- Компонент формы для пользователей ---
const UserForm = ({ initialData = {}, onSave, onCancel, allWorkers = [], isEditing = false }) => {
    const [userData, setUserData] = useState(initialData);
    const [newPassword, setNewPassword] = useState('');

    useEffect(() => {
        setUserData(initialData);
        setNewPassword('');
    }, [initialData]);

    const handleChange = (field, value) => {
        setUserData(prev => ({ ...prev, [field]: value }));
    };

    const handlePasswordChange = (e) => {
        setNewPassword(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const dataToSend = { ...userData };
        if (newPassword) {
            dataToSend.Password = newPassword;
        } else if (!isEditing) {
            alert('Пароль обязателен при создании пользователя.');
            return;
        }
        delete dataToSend.FName_Worker;
        onSave(dataToSend);
    };

    const handleCancelClick = (e) => {
        e.preventDefault();
        onCancel();
    };

    return (
        <form onSubmit={handleSubmit} className="add-form user-form">
            <h4>{isEditing ? 'Редактировать пользователя' : 'Добавить пользователя'}</h4>
            <input
                type="text" placeholder="Логин" required
                value={userData.Login || ''}
                onChange={(e) => handleChange('Login', e.target.value)} />

            <input
                type="password"
                placeholder={isEditing ? 'Новый пароль (оставьте пустым, чтобы не менять)' : 'Пароль'}
                value={newPassword}
                onChange={handlePasswordChange}
                required={!isEditing}
            />

            <select required value={userData.Role !== undefined ? userData.Role : ''} onChange={(e) => handleChange('Role', parseInt(e.target.value, 10))}>
                <option value="" disabled>Выберите роль</option>
                <option value={0}>Работник</option>
                <option value={1}>Администратор</option>
            </select>

            <select value={userData.ID_Worker || ''} onChange={(e) => handleChange('ID_Worker', e.target.value ? parseInt(e.target.value, 10) : null)}>
                <option value="">-- Не привязан к работнику --</option>
                {allWorkers.map(w => <option key={w.ID_Worker} value={w.ID_Worker}>{w.FName_Worker} (ID: {w.ID_Worker})</option>)}
            </select>

            <div className="form-actions">
               <button type="submit">Сохранить</button>
               <button type="button" onClick={handleCancelClick}>Отмена</button>
            </div>
        </form>
    );
};

function AdminPanel() {
    const [activeTab, setActiveTab] = useState('programs');
    const [programs, setPrograms] = useState([]);
    const [organizations, setOrganizations] = useState([]);
    const [kks, setKks] = useState([]);
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [editingItem, setEditingItem] = useState(null);
    const [addingItemType, setAddingItemType] = useState(null);
    const [programTypes, setProgramTypes] = useState([]);
    const [allOrganizations, setAllOrganizations] = useState([]);
    const [allWorkers, setAllWorkers] = useState([]);
    const [sortConfig, setSortConfig] = useState({ key: null, direction: 'ascending' });
    const navigate = useNavigate();
    const apiUrl = process.env.REACT_APP_API_URL;

    const user = useMemo(() => {
        const storedUser = localStorage.getItem('user');
        try {
            return storedUser ? JSON.parse(storedUser) : null;
        } catch (e) {
            console.error("Failed to parse user from localStorage", e);
            return null;
        }
    }, []);

    const requestSort = (key) => {
        let direction = 'ascending';
        if (sortConfig.key === key && sortConfig.direction === 'ascending') {
            direction = 'descending';
        }
        setSortConfig({ key, direction });
    };

    const getIdKeyForTab = (tab) => {
        switch (tab) {
            case 'programs': return 'ID_ProgDPO';
            case 'organizations': return 'ID_Org';
            case 'kks': return 'ID_KKS';
            case 'users': return 'ID_User';
            default: return null;
        }
    };

    useEffect(() => {
        const idKey = getIdKeyForTab(activeTab);
        if (idKey) {
            setSortConfig({ key: idKey, direction: 'ascending' });
        } else {
            setSortConfig({ key: null, direction: 'ascending' });
        }
    }, [activeTab]);

    const fetchData = useCallback(async (forceAux = false) => {
        if (!user || user.role !== 1) {
            console.warn("fetchData called without admin user, aborting.");
            setLoading(false);
            return;
        }
        setLoading(true);
        setError('');
        console.log(`WorkspaceData triggered. Active tab: ${activeTab}`);

        if (forceAux || programTypes.length === 0 || allWorkers.length === 0) {
            try {
                console.log("Fetching auxiliary data (types, orgs, workers)...");
                const endpoints = [
                    `${apiUrl}/api/program-types`,
                    `${apiUrl}/api/organizations`,
                    `${apiUrl}/api/workers`
                ];
                const [typesRes, orgsRes, workersRes] = await Promise.all(
                    endpoints.map(url => axios.get(url))
                );
                setProgramTypes(typesRes.data);
                setAllOrganizations(orgsRes.data);
                setAllWorkers(workersRes.data);
                console.log("Auxiliary data fetched.");
            } catch (err) {
                console.error('Error fetching auxiliary data:', err);
                setError(prev => prev ? `${prev}\nОшибка загрузки вспомогательных данных.` : 'Ошибка загрузки вспомогательных данных.');
            }
        }

        try {
            let response;
            let endpoint;
            switch (activeTab) {
                case 'programs': endpoint = `${apiUrl}/api/programs`; break;
                case 'organizations': endpoint = `${apiUrl}/api/organizations`; break;
                case 'kks': endpoint = `${apiUrl}/api/kks`; break;
                case 'users': endpoint = `${apiUrl}/api/users`; break;
                default:
                    console.warn(`Unknown activeTab: ${activeTab}`);
                    setLoading(false);
                    return;
            }
            console.log(`Attempting to fetch ${endpoint}...`);
            response = await axios.get(endpoint);
            console.log(`Received response from ${endpoint}`, response.status);
            switch (activeTab) {
                case 'programs': setPrograms(response.data); break;
                case 'organizations': setOrganizations(response.data); break;
                case 'kks': setKks(response.data); break;
                case 'users': setUsers(response.data); break;
            }
        } catch (err) {
            console.error(`Error fetching ${activeTab}:`, err.response || err.message || err);
            setError(prev => prev ? `${prev}\nОшибка загрузки данных для ${activeTab}.` : `Ошибка загрузки данных для ${activeTab}.`);
        } finally {
            console.log(`Setting loading to false for ${activeTab}.`);
            setLoading(false);
        }
    }, [activeTab, user, navigate, apiUrl, programTypes.length, allWorkers.length]);

    useEffect(() => {
        if (!user || user.role !== 1) {
            navigate('/login');
            return;
        }
        fetchData(true);
    }, [user, navigate, fetchData]);

    const sortedPrograms = useMemo(() => {
        let sortableItems = [...programs];
        if (sortConfig.key) {
            sortableItems.sort((a, b) => {
                if (a[sortConfig.key] < b[sortConfig.key]) {
                    return sortConfig.direction === 'ascending' ? -1 : 1;
                }
                if (a[sortConfig.key] > b[sortConfig.key]) {
                    return sortConfig.direction === 'ascending' ? 1 : -1;
                }
                return 0;
            });
        }
        return sortableItems;
    }, [programs, sortConfig]);

    const sortedOrganizations = useMemo(() => {
        let sortableItems = [...organizations];
        if (sortConfig.key) {
            sortableItems.sort((a, b) => {
                const valA = a[sortConfig.key];
                const valB = b[sortConfig.key];
                const compareResult = typeof valA === 'number' && typeof valB === 'number'
                    ? valA - valB
                    : String(valA).localeCompare(String(valB));
                return sortConfig.direction === 'ascending' ? compareResult : -compareResult;
            });
        }
        return sortableItems;
    }, [organizations, sortConfig]);

    const sortedKks = useMemo(() => {
        let sortableItems = [...kks];
        if (sortConfig.key) {
            sortableItems.sort((a, b) => {
                const valA = a[sortConfig.key];
                const valB = b[sortConfig.key];
                const compareResult = typeof valA === 'number' && typeof valB === 'number'
                    ? valA - valB
                    : String(valA).localeCompare(String(valB));
                return sortConfig.direction === 'ascending' ? compareResult : -compareResult;
            });
        }
        return sortableItems;
    }, [kks, sortConfig]);

    const sortedUsers = useMemo(() => {
        let sortableItems = [...users];
        if (sortConfig.key) {
            sortableItems.sort((a, b) => {
                const valA = a[sortConfig.key];
                const valB = b[sortConfig.key];

                if (sortConfig.key === 'Role' && typeof valA === 'number' && typeof valB === 'number') {
                    return sortConfig.direction === 'ascending' ? valA - valB : valB - valA;
                }
                if (sortConfig.key === 'FName_Worker') {
                    const nameA = String(valA ?? '').toLowerCase();
                    const nameB = String(valB ?? '').toLowerCase();
                    if (nameA < nameB) return sortConfig.direction === 'ascending' ? -1 : 1;
                    if (nameA > nameB) return sortConfig.direction === 'ascending' ? 1 : -1;
                    return 0;
                }

                const compareResult = typeof valA === 'number' && typeof valB === 'number'
                    ? valA - valB
                    : String(valA ?? '').localeCompare(String(valB ?? ''));

                return sortConfig.direction === 'ascending' ? compareResult : -compareResult;
            });
        }
        return sortableItems;
    }, [users, sortConfig]);

    const handleEdit = (item) => {
        setEditingItem(item);
        setAddingItemType(null);
    };

    const handleCancel = () => {
        setEditingItem(null);
    };

    const handleChange = (field, value) => {
        setEditingItem(prev => ({ ...prev, [field]: value }));
    };

    const handleSave = async (editedData) => {
        const itemToSave = editedData || editingItem;
        if (!itemToSave) return;

        const idKey = getIdKeyForTab(activeTab);
        const id = itemToSave[idKey];
        if (id === undefined) {
            console.error("Cannot save item without ID");
            setError("Не удалось определить ID для сохранения.");
            return;
        }

        console.log(`Saving edited item (ID: ${id}):`, itemToSave);
        setLoading(true);
        try {
            let endpoint;
            switch (activeTab) {
                case 'programs': endpoint = `${apiUrl}/api/programs/${id}`; break;
                case 'organizations': endpoint = `${apiUrl}/api/organizations/${id}`; break;
                case 'kks': endpoint = `${apiUrl}/api/kks/${id}`; break;
                case 'users': endpoint = `${apiUrl}/api/users/${id}`; break;
                default: throw new Error("Неизвестная вкладка для сохранения");
            }
            await axios.put(endpoint, itemToSave);
            setEditingItem(null);
            await fetchData();
        } catch (err) {
            console.error('Ошибка при сохранении изменений:', err);
            setError(err.response?.data?.error || 'Ошибка при сохранении данных');
        } finally {
            setLoading(false);
        }
    };

    const handleShowAddForm = () => {
        setAddingItemType(activeTab);
        setEditingItem(null);
    };

    const handleCancelAdd = () => {
        setAddingItemType(null);
    };

    const handleAddItem = async (newItemData) => {
        const type = addingItemType;
        if (!type) return;
        console.log(`Adding new ${type}:`, newItemData);
        setLoading(true);
        try {
            let endpoint;
            switch (type) {
                case 'programs': endpoint = `${apiUrl}/api/programs`; break;
                case 'organizations': endpoint = `${apiUrl}/api/organizations`; break;
                case 'kks': endpoint = `${apiUrl}/api/kks`; break;
                case 'users': endpoint = `${apiUrl}/api/users`; break;
                default: throw new Error("Неизвестный тип для добавления");
            }
            await axios.post(endpoint, newItemData);
            setAddingItemType(null);
            await fetchData();
        } catch (err) {
            console.error(`Ошибка при добавлении ${type}:`, err);
            setError(err.response?.data?.error || `Ошибка при добавлении элемента`);
        } finally {
            setLoading(false);
        }
    };

    const handleDelete = async (idToDelete) => {
        if (!window.confirm('Вы уверены, что хотите удалить этот элемент?')) {
            return;
        }
        console.log(`Deleting item with ID: ${idToDelete} on tab: ${activeTab}`);
        setLoading(true);
        try {
            let endpoint;
            switch (activeTab) {
                case 'programs': endpoint = `${apiUrl}/api/programs/${idToDelete}`; break;
                case 'organizations': endpoint = `${apiUrl}/api/organizations/${idToDelete}`; break;
                case 'kks': endpoint = `${apiUrl}/api/kks/${idToDelete}`; break;
                case 'users': endpoint = `${apiUrl}/api/users/${idToDelete}`; break;
                default: throw new Error("Неизвестная вкладка для удаления");
            }
            await axios.delete(endpoint);
            if (editingItem && editingItem[getIdKeyForTab(activeTab)] === idToDelete) {
                setEditingItem(null);
            }
            await fetchData();
        } catch (err) {
            console.error(`Ошибка при удалении элемента (ID: ${idToDelete}):`, err);
            setError(err.response?.data?.error || 'Ошибка при удалении элемента');
        } finally {
            setLoading(false);
        }
    };

    if (!user && loading && !programTypes.length) return <div className="loading">Проверка авторизации и загрузка данных...</div>;
    if (!user) return null;
    const showLoadingIndicator = loading && !error;

    const displayRole = (roleValue) => {
        return roleValue === 1 ? 'Работник ЦДО' : roleValue === 0 ? 'Работник ОУ' : 'Неизвестно';
    };

    const renderSortableHeader = (label, key) => (
        <th onClick={() => requestSort(key)} className="sortable-header">
            {label}
            {sortConfig.key === key ? (sortConfig.direction === 'ascending' ? ' ▲' : ' ▼') : ' ↕'}
        </th>
    );

    return (
        <div className="admin-panel">
            <div className="admin-header">
                <h1>Панель администратора</h1>
                <div className="header-buttons">
                    <button onClick={() => navigate(user?.role === 1 ? '/admin' : '/user')} className="back-button">Назад</button>
                    <button onClick={() => { localStorage.removeItem('user'); navigate('/login'); }} className="logout-button">Выйти</button>
                </div>
            </div>

            <div className="admin-tabs">
                <button 
                    className={`tab-button ${activeTab === 'programs' ? 'active' : ''}`}
                    onClick={() => setActiveTab('programs')}
                >
                    Программы
                </button>
                <button 
                    className={`tab-button ${activeTab === 'organizations' ? 'active' : ''}`}
                    onClick={() => setActiveTab('organizations')}
                >
                    Организации
                </button>
                <button 
                    className={`tab-button ${activeTab === 'kks' ? 'active' : ''}`}
                    onClick={() => setActiveTab('kks')}
                >
                    ККС
                </button>
                <button
                    className={`tab-button ${activeTab === 'users' ? 'active' : ''}`}
                    onClick={() => setActiveTab('users')}
                >
                    Пользователи
                </button>
            </div>

            {showLoadingIndicator && <div className="loading-inline">Обновление данных...</div>}
            {error && <div className="error-inline">{error} <button onClick={() => setError('')}>×</button></div>}

            <div className="admin-content">
                {(addingItemType === 'users' || (editingItem && activeTab === 'users')) && (
                    <UserForm
                        key={editingItem ? editingItem.ID_User : 'add'}
                        initialData={editingItem || {}}
                        onSave={editingItem ? handleSave : handleAddItem}
                        onCancel={editingItem ? handleCancel : handleCancelAdd}
                        allWorkers={allWorkers}
                        isEditing={!!editingItem}
                    />
                )}

                {!editingItem && !addingItemType && (
                    <button onClick={handleShowAddForm} className="add-button">
                        Добавить {
                            activeTab === 'programs' ? 'программу' :
                            activeTab === 'organizations' ? 'организацию' :
                            activeTab === 'kks' ? 'KKS' :
                            activeTab === 'users' ? 'пользователя' : ''
                        }
                    </button>
                )}

                {activeTab === 'programs' && (
                    <table className="admin-table">
                        <thead>
                            <tr>
                                {renderSortableHeader('ID', 'ID_ProgDPO')}
                                {renderSortableHeader('Название', 'Name_ProgDPO')}
                                {renderSortableHeader('Тип', 'Name_Type')}
                                <th>Действия</th>
                            </tr>
                        </thead>
                        <tbody>
                            {sortedPrograms.map(program => (
                                <tr key={program.ID_ProgDPO}>
                                    {editingItem?.ID_ProgDPO === program.ID_ProgDPO ? (
                                        <>
                                            <td>{program.ID_ProgDPO}</td>
                                            <td>
                                                <input
                                                    type="text"
                                                    value={editingItem.Name_ProgDPO}
                                                    onChange={(e) => handleChange('Name_ProgDPO', e.target.value)}
                                                />
                                            </td>
                                            <td>
                                                <input
                                                    type="text"
                                                    value={editingItem.Name_Type}
                                                    onChange={(e) => handleChange('Name_Type', e.target.value)}
                                                />
                                            </td>
                                            <td>
                                                <button onClick={handleSave}>Сохранить</button>
                                                <button onClick={handleCancel}>Отмена</button>
                                            </td>
                                        </>
                                    ) : (
                                        <>
                                            <td>{program.ID_ProgDPO}</td>
                                            <td>{program.Name_ProgDPO}</td>
                                            <td>{program.Name_Type}</td>
                                            <td className="actions-cell">
                                                <button onClick={() => handleEdit(program)} disabled={!!addingItemType}>Редакт.</button>
                                                <button onClick={() => handleDelete(program.ID_ProgDPO)} className="delete-button" disabled={!!addingItemType || !!editingItem}>Удалить</button>
                                            </td>
                                        </>
                                    )}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}

                {activeTab === 'organizations' && (
                    <table className="admin-table">
                        <thead>
                            <tr>
                                {renderSortableHeader('ID', 'ID_Org')}
                                {renderSortableHeader('Полное название', 'FName')}
                                {renderSortableHeader('Краткое название', 'SName')}
                                <th>Действия</th>
                            </tr>
                        </thead>
                        <tbody>
                            {sortedOrganizations.map(org => (
                                <tr key={org.ID_Org}>
                                    {editingItem?.ID_Org === org.ID_Org ? (
                                        <>
                                            <td>{org.ID_Org}</td>
                                            <td>
                                                <input
                                                    type="text"
                                                    value={editingItem.FName}
                                                    onChange={(e) => handleChange('FName', e.target.value)}
                                                />
                                            </td>
                                            <td>
                                                <input
                                                    type="text"
                                                    value={editingItem.SName}
                                                    onChange={(e) => handleChange('SName', e.target.value)}
                                                />
                                            </td>
                                            <td>
                                                <button onClick={handleSave}>Сохранить</button>
                                                <button onClick={handleCancel}>Отмена</button>
                                            </td>
                                        </>
                                    ) : (
                                        <>
                                            <td>{org.ID_Org}</td>
                                            <td>{org.FName}</td>
                                            <td>{org.SName}</td>
                                            <td className="actions-cell">
                                                <button onClick={() => handleEdit(org)} disabled={!!addingItemType}>Редакт.</button>
                                                <button onClick={() => handleDelete(org.ID_Org)} className="delete-button" disabled={!!addingItemType || !!editingItem}>Удалить</button>
                                            </td>
                                        </>
                                    )}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}

                {activeTab === 'kks' && (
                    <table className="admin-table">
                        <thead>
                            <tr>
                                {renderSortableHeader('ID', 'ID_KKS')}
                                {renderSortableHeader('Название', 'Name_KKS')}
                                {renderSortableHeader('Краткое обозначение', 'SName_KKS')}
                                <th>Действия</th>
                            </tr>
                        </thead>
                        <tbody>
                            {sortedKks.map(kksItem => (
                                <tr key={kksItem.ID_KKS}>
                                    {editingItem?.ID_KKS === kksItem.ID_KKS ? (
                                        <>
                                            <td>{kksItem.ID_KKS}</td>
                                            <td>
                                                <input
                                                    type="text"
                                                    value={editingItem.Name_KKS}
                                                    onChange={(e) => handleChange('Name_KKS', e.target.value)}
                                                />
                                            </td>
                                            <td>
                                                <input
                                                    type="text"
                                                    value={editingItem.SName_KKS}
                                                    onChange={(e) => handleChange('SName_KKS', e.target.value)}
                                                />
                                            </td>
                                            <td>
                                                <button onClick={handleSave}>Сохранить</button>
                                                <button onClick={handleCancel}>Отмена</button>
                                            </td>
                                        </>
                                    ) : (
                                        <>
                                            <td>{kksItem.ID_KKS}</td>
                                            <td>{kksItem.Name_KKS}</td>
                                            <td>{kksItem.SName_KKS}</td>
                                            <td className="actions-cell">
                                                <button onClick={() => handleEdit(kksItem)} disabled={!!addingItemType}>Редакт.</button>
                                                <button onClick={() => handleDelete(kksItem.ID_KKS)} className="delete-button" disabled={!!addingItemType || !!editingItem}>Удалить</button>
                                            </td>
                                        </>
                                    )}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}

                {activeTab === 'users' && (
                    <table className="admin-table">
                        <thead>
                            <tr>
                                {renderSortableHeader('ID', 'ID_User')}
                                {renderSortableHeader('Логин', 'Login')}
                                {renderSortableHeader('Роль', 'Role')}
                                {renderSortableHeader('Работник', 'FName_Worker')}
                                <th>Действия</th>
                            </tr>
                        </thead>
                        <tbody>
                            {sortedUsers.map(u => (
                                <tr key={u.workerId}>
                                    <>
                                        <td>{u.workerId}</td>
                                        <td>{u.Login}</td>
                                        <td>{displayRole(u.Role)}</td>
                                        <td>{u.FName_Worker || '--'}</td>
                                        <td className="actions-cell">
                                            <button onClick={() => handleEdit(u)} disabled={!!addingItemType || !!editingItem}>Редакт.</button>
                                            <button onClick={() => handleDelete(u.workerId)} className="delete-button" disabled={!!addingItemType || !!editingItem}>Удалить</button>
                                        </td>
                                    </>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );
}

export default AdminPanel;
