import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useData } from '../context/DataContext';
import './Documents.css';

// Компонент для отображения таблицы документов
function DocumentsTable({ documents, onShowDetails }) {
  return (
    <table className="documents-table">
      <thead>
        <tr>
          <th>Программа</th>
          <th>Организация</th>
          <th>Дата выдачи</th>
          <th>Действия</th>
        </tr>
      </thead>
      <tbody>
        {documents.map((doc) => (
          <tr key={doc.ID_Document}>
            <td>{doc.ProgramName}</td>
            <td>{doc.SName}</td>
            <td>{new Date(doc.DateIssue).toLocaleDateString()}</td>
            <td>
              <button 
                className="details-button"
                onClick={() => onShowDetails(doc)}
              >
                Подробности
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

// Компонент для отображения деталей документа в модальном окне
function DocumentDetails({ document, programDetails }) {
  return (
    <div className="modal-details">
      <div className="detail-group">
        <h3>Основная информация</h3>
        <p><strong>Программа:</strong> {document.ProgramName}</p>
        <p><strong>Тип программы:</strong> {programDetails?.Name_Type}</p>
        <p><strong>Краткое название организации:</strong> {programDetails?.SName}</p>
        <p><strong>Дата выдачи:</strong> {new Date(document.DateIssue).toLocaleDateString()}</p>
        <p><strong>Регистрационный номер:</strong> {document.regnumber}</p>
        <p><strong>Серия формы:</strong> {document.formseries || '-'}</p>
        <p><strong>Номер формы:</strong> {document.formnumber || '-'}</p>
        <p><strong>Дата начала:</strong> {new Date(document.DataStart).toLocaleDateString()}</p>
        <p><strong>Дата окончания:</strong> {new Date(document.DataEnd).toLocaleDateString()}</p>
      </div>
      <div className="detail-group">
        <h3>Критерии ККС</h3>
        {programDetails?.kksStatus && programDetails.kksStatus.length > 0 ? (
          <div>
            {programDetails.kksStatus.map((kks) => (
              <div key={kks.id} className="kks-item">
                <p><strong>ККС:</strong> {kks.name}</p>
                <p><strong>Краткое обозначение:</strong> {kks.shortName}</p>
                <p><strong>Последнее обучение:</strong> {kks.lastTrainingDate ? new Date(kks.lastTrainingDate).toLocaleDateString() : 'Нет данных'}</p>
                {kks.needsTraining && (
                  <div className="training-alert">
                    ⚠️ Требуется обучение! Последнее обучение было более 3 лет назад.
                  </div>
                )}
              </div>
            ))}
          </div>
        ) : (
          <p>Критерии ККС не указаны</p>
        )}
      </div>
    </div>
  );
}

// Компонент модального окна
function DocumentModal({ document, programDetails, onClose }) {
  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="modal-close" onClick={onClose}>×</button>
        <h2>Подробная информация о документе</h2>
        <DocumentDetails document={document} programDetails={programDetails} />
      </div>
    </div>
  );
}

// Основной компонент
function Documents() {
  const [showModal, setShowModal] = useState(false);
  const [selectedDocument, setSelectedDocument] = useState(null);
  const [programDetails, setProgramDetails] = useState(null);
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));
  const { documents, loading, error, fetchUserDocuments, fetchProgramDetails } = useData();

  useEffect(() => {
    if (user?.id) {
      fetchUserDocuments(user.id);
    }
  }, [user?.id, fetchUserDocuments]);

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
  };

  const handleBack = () => {
    navigate('/user');
  };

  const handleShowDetails = async (document) => {
    try {
      if (!document?.ID_ProgDPO) {
        console.error('Ошибка: отсутствует ID_ProgDPO у документа', document);
        return;
      }
      
      const details = await fetchProgramDetails(document.ID_ProgDPO, user.id);
      setProgramDetails(details);
      setSelectedDocument(document);
      setShowModal(true);
    } catch (err) {
      console.error('Ошибка при загрузке деталей программы:', err);
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setSelectedDocument(null);
    setProgramDetails(null);
  };

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="documents-container">
      <div className="documents-header">
        <h1>Мои документы</h1>
        <div className="header-buttons">
          <button onClick={handleBack} className="back-button">Назад</button>
          <button onClick={handleLogout} className="logout-button">Выйти</button>
        </div>
      </div>
      
      <div className="documents-list">
        {documents.length === 0 ? (
          <p className="no-documents">Документы не найдены</p>
        ) : (
          <DocumentsTable 
            documents={documents} 
            onShowDetails={handleShowDetails}
          />
        )}
      </div>

      {showModal && selectedDocument && (
        <DocumentModal 
          document={selectedDocument}
          programDetails={programDetails}
          onClose={handleCloseModal}
        />
      )}
    </div>
  );
}

export default Documents;
