import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Import axios for making HTTP requests
import './User.css';
const apiUrl = process.env.REACT_APP_API_URL
// Helper function to check if training is needed
const isTrainingNeeded = (lastTrainingDateStr) => {
  if (!lastTrainingDateStr) {
    return true; // No training date found, assume training is needed
  }
  try {
    const lastTrainingDate = new Date(lastTrainingDateStr);
    const threeYearsAgo = new Date();
    threeYearsAgo.setFullYear(threeYearsAgo.getFullYear() - 3);

    // Check if the date is valid before comparing
    if (isNaN(lastTrainingDate.getTime())) {
        console.warn("Invalid date encountered:", lastTrainingDateStr);
        return true; // Treat invalid date as needing training
    }

    return lastTrainingDate < threeYearsAgo;
  } catch (e) {
    console.error("Error parsing date:", lastTrainingDateStr, e);
    return true; // Error parsing date, assume training needed
  }
};

// Helper function to format date for display
const formatDate = (dateStr) => {
    if (!dateStr) return 'Нет данных';
    try {
        const date = new Date(dateStr);
        if (isNaN(date.getTime())) return 'Некорректная дата';
        // Format to DD.MM.YYYY
        return date.toLocaleDateString('ru-RU');
    } catch (e) {
        return 'Ошибка формата';
    }
}

function User() {
  const navigate = useNavigate();
  const [workers, setWorkers] = useState([]);
  const user = JSON.parse(localStorage.getItem('user'));
  const [kksStatus, setKksStatus] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const getCurrentWorkerName = useCallback(() => {
    // Проверяем, что пользователь является работником (role !== 0) и что workers - массив
    if (user?.role === 0 || !user?.ID_Worker || !Array.isArray(workers)) return '';
    // Находим текущего работника по ID из user
    const currentWorker = workers.find(w => w.ID_Worker === user.ID_Worker);
    // Возвращаем ФИО, если работник найден, или его ID, если нет
    return currentWorker 
        ? `${currentWorker.FName_Worker} ${currentWorker.LName_Worker} ${currentWorker.MName_Worker || ''}`.trim()
        : `ID ${user.ID_Worker}`;
  }, [user?.role, user?.ID_Worker, workers]);

  useEffect(() => {
    const fetchKksStatus = async () => {
      // Ensure user and user.id exist before fetching
      if (!user || !user.id) {
          console.log("User data not available in localStorage yet.");
          // Optionally set an error or return
          setError("Данные пользователя не найдены. Пожалуйста, войдите снова.");
          return;
      }
      // Only fetch status for workers (assuming role 0 is worker)
      // Adjust condition if role logic is different
      if (user.role !== 0) {
          console.log("Пользователь не является работником (role != 0), статус ККС не запрашивается.");
          return;
      }

      if (!Array.isArray(workersResponse.data)) {
        console.error("Workers API did not return an array:", workersResponse.data);
        setWorkers([]); // Set empty array on error
      } else {
          setWorkers(workersResponse.data);
      }

      setIsLoading(true);
      setError(null);
      try {
        // Use the user ID from localStorage for the request
        const response = await axios.get(`${apiUrl}/api/user-kks-status?userId=${user.id}`);
        console.log("KKS Status Response:", response.data); // Log received data
        setKksStatus(response.data || []); // Ensure it's an array
      } catch (err) {
        console.error('Ошибка при загрузке статуса ККС:', err);
        setError(err.response?.data?.error || 'Не удалось загрузить статус обучения по ККС.');
      } finally {
        setIsLoading(false);
      }
    };

    fetchKksStatus();
    // Add user.id as a dependency if it might change, though usually it won't after login
  }, [user?.id, user?.role]); // Re-run if user.id or user.role changes


  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
  };

  const handleViewDocuments = () => {
    navigate('/documents');
  };

  const handleViewAnalysis = () => {
    navigate('/analysis');
  };
  


  // Filter KKS that require training
  const kksNeedingTraining = kksStatus.filter(kks => isTrainingNeeded(kks.LastTrainingDate));

  return (
    <div className="user-container">
      <div className="user-header">
        <h1>Личный кабинет</h1>
        <button onClick={handleLogout} className="logout-button">Выйти</button>
      </div>

      <div className="user-content">
        <div className="user-info">
          <h2>Информация о пользователе</h2>
          {/* Safely access user properties */}
          <p><strong>ФИО: </strong> {workers.WorkerName || getCurrentWorkerName() || 'N/A'}</p>
          <p><strong>Логин:</strong> {user?.login || 'Логин не найден'}</p>
          <p><strong>Роль:</strong> Работник образовательного учреждения</p>
        </div>

        {/* Conditionally render KKS Notifications only for workers */}
        {user?.role === 0 && (
            <div className="user-kks-notifications">
            <h2>Статус обучения по ККС</h2>
            {isLoading && <p>Загрузка статуса ККС...</p>}
            {error && <p className="error-message">Ошибка: {error}</p>}
            {!isLoading && !error && (
                <>
                {kksNeedingTraining.length > 0 ? (
                    <>
                    <p className="notification-warning"><strong>Внимание!</strong> Требуется пройти обучение по следующим критериям ККС:</p>
                    <ul>
                        {kksNeedingTraining.map(kks => (
                        <li key={kks.ID_KKS}>
                            <strong>{kks.KKS_FullName} ({kks.KKS_ShortName})</strong>
                            <br />
                            <small>Последнее обучение: {formatDate(kks.LastTrainingDate)} </small>
                        </li>
                        ))}
                    </ul>
                    </>
                ) : (
                    <p className="notification-success">Все необходимые обучения по ККС пройдены своевременно.</p>
                )}
                {kksStatus.length === 0 && !isLoading && <p>Нет данных о прохождении обучения по ККС для этого пользователя.</p>}
                </>
            )}
            </div>
        )}


        <div className="user-actions">
          <button onClick={handleViewDocuments} className="action-button">
            Просмотр Моих Документов
          </button>
            <button onClick={handleViewAnalysis} className="action-button">
            Просмотр анализа
          </button>
        </div>
      </div>
    </div>
  );
}

export default User;
