import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import { Bar } from 'react-chartjs-2';
import './DocumentAnalysis.css';

// Регистрируем компоненты Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function DocumentAnalysis() {
  const [documents, setDocuments] = useState([]);
  const [workers, setWorkers] = useState([]);
  const [selectedWorker, setSelectedWorker] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [viewMode, setViewMode] = useState('analysis'); // 'analysis' или 'documents'
  const [selectedDocument, setSelectedDocument] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [programDetails, setProgramDetails] = useState(null); // For modal only
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true); // Start loading
      setError(''); // Reset error
      try {
        // Загрузка документов
        const documentsResponse = await axios.get(`http://localhost:5000/api/documents`, {
          params: {
            userId: user.id,
            role: user.role
          }
        });
        console.log("Frontend: Raw Axios response data:", JSON.stringify(documentsResponse.data, null, 2));
        if (!Array.isArray(documentsResponse.data)) {
          console.error('Ожидался массив документов, получено:', documentsResponse.data);
          setDocuments([]);
        } else {
          setDocuments(documentsResponse.data);
        }

        console.log("Full API response:", documentsResponse.data);
        if (documentsResponse.data && documentsResponse.data.length > 0) {
          console.log("First document structure:", documentsResponse.data[0]);
          console.log("All document keys:", Object.keys(documentsResponse.data[0]));
        }
        console.log("Fetched documents data (RAW):", documentsResponse.data);
        if (documentsResponse.data && documentsResponse.data.length > 0) {
          console.log("Sample KKS_Names in first fetched document:", documentsResponse.data[0]?.KKS_Names);
        }
        setDocuments(documentsResponse.data || []);
        /*
        console.log("Fetched documents:", documentsResponse.data); // Log fetched data
        // Check if KKS_Names exists in the first document (for verification)
        if (documentsResponse.data.length > 0) {
            console.log("KKS_Names in first document:", documentsResponse.data[0]?.KKS_Names);
        }
        setDocuments(documentsResponse.data);
        */
        // Если пользователь - работник ЦДО, загружаем список работников
        if (user.role === 1) {
          const workersResponse = await axios.get('http://localhost:5000/api/workers');
          setWorkers(workersResponse.data);
          if (workersResponse.data.length > 0) {
            // Automatically select the first worker if none is selected or keep the current selection
            setSelectedWorker(prevWorker => prevWorker ?? workersResponse.data[0].ID_Worker);
          } else {
             setSelectedWorker(null); // No workers available
          }
        } else {
            // For regular users, no worker selection needed
            setSelectedWorker(null);
        }

        setLoading(false);
      } catch (err) {
        console.error('Ошибка при загрузке данных:', err);
        setError(err.response?.data?.error || 'Ошибка при загрузке данных');
        setLoading(false);
      }
    };

    if (user?.id) { // Ensure user info is available
        fetchData();
    } else {
        setError("Пользователь не найден. Пожалуйста, войдите снова.");
        setLoading(false);
        // Optional: redirect to login
        // navigate('/login');
    }
  }, [user?.id, user?.role]); // Depend on user.id and user.role

  const handleLogout = () => {
    localStorage.removeItem('user');
    navigate('/login');
  };

  const handleBack = () => {
    navigate(user.role === 1 ? '/admin' : '/user');
  };

  const handleWorkerChange = (event) => {
    const workerId = parseInt(event.target.value, 10);
    setSelectedWorker(workerId);
  };

  const handleViewModeChange = (mode) => {
    setViewMode(mode);
  };

  // Function to fetch details for the modal (keeps KKS fetch separate for modal)
  const fetchModalProgramDetails = async (progId) => {
      try {
          const response = await axios.get(`http://localhost:5000/api/program-details/${progId}`);
          console.log('Полученные детали программы для модального окна:', response.data);
          return response.data;
      } catch (err) {
          console.error('Ошибка при загрузке деталей программы для модального окна:', err);
          console.error('Детали ошибки:', err.response?.data || err.message);
          return null; // Return null on error
      }
  }

  const handleShowDetails = async (document) => {
    if (!document || !document.ID_ProgDPO) {
      console.error('Ошибка: отсутствует ID_ProgDPO у документа', document);
      return;
    }
    setSelectedDocument(document); // Set selected document first

    // Fetch *fresh* details specifically for the modal display
    const details = await fetchModalProgramDetails(document.ID_ProgDPO);
    setProgramDetails(details); // Set details (might be null if fetch failed)

    setShowModal(true); // Show modal
    console.log('Модальное окно открыто:', { showModal: true, selectedDocument: document, programDetails: details });
  };


  const handleCloseModal = () => {
    setShowModal(false);
    setSelectedDocument(null);
    setProgramDetails(null);
  };

  // Фильтрация документов по выбранному работнику (или все для пользователя)
  const filteredDocuments = React.useMemo(() => {
      if (loading) return []; // Return empty if still loading
      if (user.role === 1) {
          // Admin view: filter by selected worker
          if (selectedWorker === null && workers.length > 0) {
              // If no worker is selected but workers exist (e.g., initial load), default to first
              // This case might be handled by useEffect setting initial worker, but added as safeguard
               return documents.filter(doc => doc.ID_Worker === workers[0]?.ID_Worker);
          }
          return documents.filter(doc => doc.ID_Worker === selectedWorker);
      } else {
          // Regular user view: show their documents (assuming API filters by userId)
          return documents;
      }
  }, [documents, selectedWorker, user.role, loading, workers]); // Add dependencies


  // --- Подготовка данных для графиков ---

  // 1. График по годам и нарастающий итог
  const prepareYearlyChartData = (docs) => {
    const documentsByYear = docs.reduce((acc, doc) => {
        // Handle potentially invalid dates
        try {
            const date = new Date(doc.DateIssue);
            if (!isNaN(date.getTime())) { // Check if date is valid
                const year = date.getFullYear();
                acc[year] = (acc[year] || 0) + 1;
            } else {
                 console.warn("Invalid DateIssue found for document:", doc.ID_Document);
            }
        } catch (e) {
            console.warn("Error parsing DateIssue for document:", doc.ID_Document, e);
        }
      return acc;
    }, {});

    const years = Object.keys(documentsByYear).map(Number).sort((a, b) => a - b); // Sort years
    const minYear = years.length > 0 ? Math.min(...years) : new Date().getFullYear();
    const maxYear = years.length > 0 ? Math.max(...years) : new Date().getFullYear();
    const currentYear = new Date().getFullYear();
    const endYear = Math.max(maxYear, currentYear); // Ensure graph goes up to at least current year

    const allYears = [];
    // Ensure minYear is valid before looping
     if (minYear <= endYear) {
        for (let year = minYear; year <= endYear; year++) {
            allYears.push(year);
        }
     } else if (years.length === 0) {
         // If no documents, just show current year
         allYears.push(currentYear);
     }


    const counts = allYears.map(year => documentsByYear[year] || 0);

    const cumulativeCounts = counts.reduce((acc, count, index) => {
      acc.push(index === 0 ? count : (acc[index - 1] || 0) + count); // Ensure acc[index-1] exists
      return acc;
    }, []);

    return {
      years: allYears,
      counts,
      cumulativeCounts
    };
  };

  /*
  // 2. График по критериям ККС
  const prepareKKSChartData = (docs) => {
    const kksCounts = docs.reduce((acc, doc) => {
      // *** ИСПОЛЬЗУЕМ ПРЕДПОЛАГАЕМОЕ ПОЛЕ KKS_Names ***
      const kksString = doc.KKS_Names; // Assumes this field exists from the API
      console.log(`Processing Doc ID: ${doc.ID_Document}, KKS_Names received: '${kksString}' (Type: ${typeof kksString})`);
      if (kksString && typeof kksString === 'string') {
        const criteria = kksString.split(',') // Split by comma
                           .map(kks => kks.trim()) // Trim whitespace
                           .filter(kks => kks !== ''); // Remove empty strings

        criteria.forEach(criterion => {
          acc[criterion] = (acc[criterion] || 0) + 1;
        });
      } else if (kksString) {
          console.warn("KKS_Names is not a string for document:", doc.ID_Document, "Value:", kksString);
      }
      // If KKS_Names is null, undefined or empty, skip this document for KKS chart.

      return acc;
    }, {});

    const sortedCriteria = Object.keys(kksCounts).sort(); // Sort criteria names alphabetically
    const counts = sortedCriteria.map(criterion => kksCounts[criterion]);

    return {
      criteria: sortedCriteria,
      counts
    };
  };
  */
 /*
  const prepareKKSChartData = (docs) => {
    const kksCounts = docs.reduce((acc, doc) => {
      const kksString = doc.KKS_Names;
      
      if (!kksString || typeof kksString !== 'string' || kksString.trim() === '') {
        return acc;
      }
  
      const criteria = kksString.split(', ') // Используем явный разделитель
                         .map(kks => kks.trim())
                         .filter(kks => kks !== '');
  
      criteria.forEach(criterion => {
        acc[criterion] = (acc[criterion] || 0) + 1;
      });
  
      return acc;
    }, {});
  
    const sortedCriteria = Object.keys(kksCounts).sort();
    const counts = sortedCriteria.map(criterion => kksCounts[criterion]);
  
    return {
      criteria: sortedCriteria,
      counts
    };
  }; 
*/
  // Вычисляем данные для графиков на основе отфильтрованных документов
  const yearlyChartResult = prepareYearlyChartData(filteredDocuments);
  const kksChartResult = prepareKKSChartData(filteredDocuments);

  // --- Настройки для графиков ---

  const getWorkerName = () => {
      if (user.role !== 1 || !selectedWorker) return '';
      const worker = workers.find(w => w.ID_Worker === selectedWorker);
      return worker ? worker.FName_Worker : `ID ${selectedWorker}`; // Fallback to ID if name not found
  }

  const yearlyData = {
    labels: yearlyChartResult.years,
    datasets: [
      {
        label: user.role === 1
          ? `Документы (по годам) - ${getWorkerName()}`
          : 'Мои документы (по годам)',
        data: yearlyChartResult.counts,
        backgroundColor: 'rgba(54, 162, 235, 0.6)',
        borderColor: 'rgba(54, 162, 235, 1)',
        borderWidth: 1,
      },
    ],
  };

  const cumulativeData = {
    labels: yearlyChartResult.years,
    datasets: [
      {
        label: user.role === 1
          ? `Нарастающий график - ${getWorkerName()}`
          : 'Мой нарастающий график',
        data: yearlyChartResult.cumulativeCounts,
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
    ],
  };

  const kksData = {
    labels: kksChartResult.criteria,
    datasets: [
      {
        label: user.role === 1
          ? `Документы по критериям ККС - ${getWorkerName()}`
          : 'Мои документы по критериям ККС',
        data: kksChartResult.counts,
        backgroundColor: 'rgba(255, 159, 64, 0.6)', // Orange color
        borderColor: 'rgba(255, 159, 64, 1)',
        borderWidth: 1,
      },
    ],
  };

  const commonOptions = (titleText) => ({
    responsive: true,
    maintainAspectRatio: false, // Allow chart to shrink vertically if needed
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: titleText, // Dynamic title passed as argument
      },
      tooltip: {
          mode: 'index',
          intersect: false,
      }
    },
    scales: {
      x: {
          title: {
              display: true,
              text: titleText.includes('годам') ? 'Год' : (titleText.includes('ККС') ? 'Критерий ККС' : ''), // Dynamic X-axis label
          }
      },
      y: {
        beginAtZero: true,
        title: {
            display: true,
            text: 'Количество документов',
        },
        ticks: {
             // Ensure only whole numbers are shown on Y-axis for counts
            stepSize: 1,
            precision: 0
        }
      },
    },
  });

  const yearlyOptions = commonOptions(
    user.role === 1
      ? `Количество документов по годам (${getWorkerName()})`
      : 'Мои документы по годам'
  );
  const cumulativeOptions = commonOptions(
    user.role === 1
      ? `Нарастающий итог документов по годам (${getWorkerName()})`
      : 'Мой нарастающий итог документов по годам'
  );
   const kksOptions = commonOptions(
    user.role === 1
      ? `Распределение документов по критериям ККС (${getWorkerName()})`
      : 'Мое распределение документов по критериям ККС'
  );

  // --- Рендеринг ---

  if (loading) return <div className="loading">Загрузка данных...</div>;
  if (error) return <div className="error-container"><div className="error">{error}</div><button onClick={handleBack} className="back-button">Назад</button></div>; // Show back button with error


  // Show message if admin selected a worker with no documents
   const noDocumentsForSelectedWorker = user.role === 1 && selectedWorker !== null && filteredDocuments.length === 0 && documents.length > 0;


  return (
    <div className="analysis-container">
      <div className="analysis-header">
        <h1>Анализ документов</h1>
        <div className="header-buttons">
          <button onClick={handleBack} className="back-button">Назад</button>
          <button onClick={handleLogout} className="logout-button">Выйти</button>
        </div>
      </div>

      {/* Worker Selector and View Mode Toggle (Only for Admin) */}
      {user.role === 1 && (
        <div className="controls-container">
             {workers.length > 0 ? (
                 <div className="worker-selector">
                    <label htmlFor="worker-select">Работник:</label>
                    <select
                        id="worker-select"
                        value={selectedWorker ?? ''} // Handle null case for value
                        onChange={handleWorkerChange}
                        className="worker-select"
                        disabled={loading} // Disable while loading
                    >
                        {/* Optional: Add a default "Select Worker" option if needed */}
                        {/* <option value="" disabled>-- Выберите работника --</option> */}
                        {workers.map(worker => (
                        <option key={worker.ID_Worker} value={worker.ID_Worker}>
                            {worker.FName_Worker} {/* Assuming FName_Worker exists */}
                        </option>
                        ))}
                    </select>
                 </div>
             ) : (
                 <p>Список работников не загружен или пуст.</p>
             )}

             {selectedWorker !== null && ( // Only show view mode if a worker is selected
                <div className="view-mode-buttons">
                    <button
                        className={`view-mode-button ${viewMode === 'analysis' ? 'active' : ''}`}
                        onClick={() => handleViewModeChange('analysis')}
                    >
                        Анализ
                    </button>
                    <button
                        className={`view-mode-button ${viewMode === 'documents' ? 'active' : ''}`}
                        onClick={() => handleViewModeChange('documents')}
                    >
                        Документы
                    </button>
                </div>
             )}
        </div>
      )}

      {/* Content Area: Charts or Document List */}
      {viewMode === 'analysis' ? (
        // --- Analysis View (Charts) ---
        <div className="charts-grid">
          {filteredDocuments.length > 0 ? (
            <>
              {/* Chart 1: Yearly Count */}
              <div className="chart-wrapper">
                <Bar options={yearlyOptions} data={yearlyData} />
              </div>

              {/* Chart 2: Cumulative Count */}
              <div className="chart-wrapper">
                <Bar options={cumulativeOptions} data={cumulativeData} />
              </div>

              {/* Chart 3: KKS Criteria */}
              <div className="chart-wrapper kks-chart-wrapper">
                {kksChartResult.criteria.length > 0 ? (
                     <Bar options={kksOptions} data={kksData} />
                 ) : (
                     <p className="no-data-message">Нет данных по критериям ККС для отображения.</p>
                 )}
              </div>
             </>
          ) : (
             // Message when no documents match the filter
            <div className="no-data-message full-width-message">
                {user.role === 1 ?
                    (selectedWorker === null ? "Пожалуйста, выберите работника для просмотра анализа." : `Нет документов для выбранного работника (${getWorkerName()}).`) :
                    "У вас пока нет загруженных документов для анализа."
                }
            </div>
          )}
        </div>
      ) : (
        // --- Documents View (Table) ---
        <div className="documents-list">
          {filteredDocuments.length > 0 ? (
            <table className="documents-table">
              <thead>
                <tr>
                  <th>Программа</th>
                  <th>Организация</th>
                  <th>Дата выдачи</th>
                  <th>Действия</th>
                </tr>
              </thead>
              <tbody>
                {filteredDocuments.map((doc) => (
                  <tr key={doc.ID_Document}>
                    <td>{doc.ProgramName || 'N/A'}</td>
                    <td>{doc.SName || 'N/A'}</td> {/* Assuming SName comes from documents API */}
                    <td>
                        {doc.DateIssue ? new Date(doc.DateIssue).toLocaleDateString() : 'N/A'}
                    </td>
                    <td>
                      <button
                        className="details-button"
                        onClick={() => handleShowDetails(doc)}
                        disabled={!doc.ID_ProgDPO} // Disable if no program ID
                      >
                        Подробности
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            // Message when no documents match the filter in table view
            <div className="no-data-message">
              {user.role === 1 ?
                 (selectedWorker === null ? "Пожалуйста, выберите работника для просмотра документов." : `Нет документов для выбранного работника (${getWorkerName()}).`) :
                 "У вас пока нет загруженных документов."
              }
            </div>
          )}
        </div>
      )}
    </div>
  );
}


/* Displaying Org info again seems redundant if already in main section */
                    /* <div className="detail-group">
                        <h3>Информация об организации</h3>
                        <p><strong>Полное название организации:</strong> {programDetails.FName || '-'}</p>
                        <p><strong>Краткое название организации:</strong> {programDetails.SName || '-'}</p>
                        <p><strong>Место проведения программы:</strong> {programDetails.FName || '-'}</p>
                    </div> */

export default DocumentAnalysis;
